import React, { useState, useRef, useEffect } from 'react';

const Canvas = ({ data, imgRefs }) => {
  const canvasRef = useRef(null);
  const [width, setWidth] = useState(0);
  const [lines, setLines] = useState([]);
  const [firstX, setFirstX] = useState(null);
  const [secondX, setSecondX] = useState(null);

  useEffect(() => {
    const updateCanvasSize = () => {
      const containerWidth = document.querySelector('.contetnt').offsetWidth;
      setWidth(containerWidth);
    };

    window.addEventListener('resize', updateCanvasSize);
    updateCanvasSize();

    return () => window.removeEventListener('resize', updateCanvasSize);
  }, []);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    if (!ctx) return;

    const ladderWidth = canvas.width;
    const ladderHeight = canvas.height;
    const numPlayers = data.length;
    const stepHeight = ladderHeight / (numPlayers + 3);

    ctx.clearRect(0, 0, ladderWidth, ladderHeight);

    ctx.strokeStyle = 'black';
    ctx.lineWidth = 5;

    let firstXTemp = null;
    let secondXTemp = null;

    // 수직선 그리기
    data.forEach((_, index) => {
      const img = imgRefs.current[index];
      if (img) {
        const imgX = img.offsetLeft + img.width / 2; // 중앙을 기준으로 X 위치 계산
        if (firstXTemp === null) {
          firstXTemp = imgX; // 첫 번째 수직선의 X 좌표 저장
        } else if (secondXTemp === null) {
          secondXTemp = imgX; // 두 번째 수직선의 X 좌표 저장
        }

        ctx.beginPath();
        ctx.moveTo(imgX, 0);
        ctx.lineTo(imgX, ladderHeight);
        ctx.stroke();
      }
    });

    if (firstXTemp !== null && secondXTemp !== null) {
      setFirstX(firstXTemp);
      setSecondX(secondXTemp);
    }

    ctx.strokeStyle = '#000';
    ctx.lineWidth = 5;

    const midX = (firstX && secondX) ? (firstX + secondX) / 2 : null;

    // 수평선 그리기
    lines.forEach((line) => {
      ctx.beginPath();
      if (midX !== null) {
        ctx.moveTo(midX, line.y);
        ctx.lineTo(ladderWidth, line.y);
      } else {
        // 중간 X 값이 없는 경우
        ctx.moveTo(line.x * (ladderWidth / width), line.y);
        ctx.lineTo(ladderWidth, line.y);
      }
      ctx.stroke();
    });
    console.log(midX)
  }, [data, imgRefs, width, lines]);

  useEffect(() => {
    const numPlayers = data.length;
    const ladderHeight = 600;
    const stepHeight = ladderHeight / (numPlayers + 3);

    const newLines = [];

    for (let i = 0; i < numPlayers + 4; i++) {
      const lineY = Math.min((i + 1) * stepHeight, 580);
      if (Math.random() > 0.5) {
        data.forEach((_, index) => {
          const img = imgRefs.current[index];
          if (img) {
            const imgX = img.offsetLeft + img.width / 2; // 중앙을 기준으로 X 위치 계산
            console.log(imgX)
            newLines.push({ x: imgX, y: lineY });
          }
        });
      }
    }

    setLines(newLines);
  }, [data, imgRefs]);

  return (
    <canvas
      ref={canvasRef}
      width={width}
      height={600}
    />
  );
};

export default Canvas;
