import { SET_IMGDATA, SET_PLAYER } from '../actions/types';
import { image } from '../../imageFile/Home'; // 이미지 배열을 가져옵니다

const allData = [
    image 
];

const initialState = {
    data: allData.slice(0, 2), // `allData`는 중첩 배열이므로, 제대로 된 데이터가 아닐 수 있습니다.
    playerCount: 2,
    players: [],
};

const imgdataReducer = (state = initialState, action) => {
    switch (action.type) {
        case SET_IMGDATA:
            return { ...state, data: action.payload };

        case "INC_PLAYERS": {
            const newPlayerCount = state.playerCount + 1;
            return {
                ...state,
                playerCount: newPlayerCount,
                data: allData.flat().slice(0, newPlayerCount), 
            };
        }

        case "DEC_PLAYERS": {
            const newPlayerCount = Math.max(2, state.playerCount - 1);
            return {
                ...state,
                playerCount: newPlayerCount,
                data: allData.flat().slice(0, newPlayerCount), 
            };
        }

        default:
            return state;
    }
};

export default imgdataReducer;
