import styled from "styled-components";

export const components = styled.div`
    display: flex;
    justify-content: center;
.body {
  background-color: #fff;
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  position: relative
}
.Start {
  font-size: 69px;
  display: flex;
  justify-content: center;
  gap: 21px;
  margin-bottom: 34px;
}

.plus {
  font-size: 40px;
  margin-left: 40px;
}
.miners {
  font-size: 40px;
  margin-right: 40px;
}
.game {
  width: 100%;
}
.parent_body {
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100%;
       height: 1000px;
    position: relative; /* For positioning the canvas */
}

.data_container {
position: relative;
    display: flex;
    flex-direction: row;
   justify-content: center;
     width: 95%;
    height: 80%;
}

.item {
    position: relative;
    display: flex;
    flex-direction: column;
   width: 485px; /* Adjust as needed */
    height: 100%; /* Adjust as needed */
}

.image {
   width: 26%;
    height: auto;
}

.input {
  width: 24%;
   margin-top: 205px;
    padding: 5px;
}

.canvas_wrapper {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
  
}

`
