import React, { useState ,useEffect} from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from "react-redux";
import { setMemo } from "../redux/actions/memoAction"; // 액션 생성자
import * as S from './style';

function Memo() {
    const location = useLocation();
    const navigate = useNavigate();
    const [inputValue, setInputValue] = useState("");
    const [textValue, setTextValue] = useState("");

    const dispatch = useDispatch();
    const memos = useSelector(state => state.memo.memo) || [];

    // 메모 상태 업데이트 함수
    const updateMemos = (newMemos) => {
        dispatch(setMemo(newMemos));
    };

    const handleInputChange = (e) => {
        setInputValue(e.target.value);  
    };

    const handleTextChange = (e) => {
        setTextValue(e.target.value);  
    };

    const handleCheckboxChange = (index) => {
        const updatedMemos = memos.map((memo, i) =>
            i === index ? { ...memo, checked: !memo.checked } : memo
        );
        updateMemos(updatedMemos);
    };

    const uploadMemo = async () => {
        if (inputValue && textValue) {
            const memoTime = new Date().toLocaleString();
            const newMemo = { title: inputValue, content: textValue, name: textValue, created_at: memoTime };
            console.log('Sending data:', JSON.stringify(newMemo));
            try {
                const response = await fetch('http://localhost:5000/api/notes', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify(newMemo),
                });
    
                const responseData = await response.json();
                console.log('Response data:', responseData);
    
                if (response.ok) {
                    setInputValue('');
                    setTextValue('');
                    navigate('/memo');
                    
                    updateMemos([...memos, responseData]);
                } else {
                    console.error('Failed to upload memo:', responseData.message);
                    alert(responseData.message);
                }
            } catch (error) {
                console.error('Error:', error);
            }
        } else {
            console.log('err');
        }
    };
    
    const deleteMemos = async () => {
        const idsToDelete = memos.filter(memo => memo.checked).map(memo => memo.id);

        for (const id of idsToDelete) {
            try {
                const response = await fetch(`http://localhost:5000/api/notes/${id}`, {
                    method: 'DELETE',
                });

                if (response.ok) {
                    console.log(`Deleted memo with id ${id}`);
                } else {
                    console.error(`Failed to delete memo with id ${id}`);
                }
            } catch (error) {
                console.error('Error:', error);
            }
        }

        updateMemos(memos.filter(memo => !memo.checked)); // 삭제된 메모를 제외한 나머지
    };
    useEffect(() => {
        const fetchMemos = async () => {
          try {
            const response = await fetch('http://localhost:5000/api/notes');
            const data = await response.json();
            dispatch(setMemo(data));
          } catch (error) {
            console.error('Error fetching memos:', error);
          }
        };
    
        fetchMemos();
      }, [dispatch]);
      
      const renderMemoList = () => (
        <>
            <div className='bar'>
                <Link to='/memo/input'>
                    <button className="w-btn w-btn-indigo" type="button">memo</button>
                </Link>
                <button onClick={deleteMemos} className="w-btn w-btn-pink" type="button">Delete</button>
            </div>
            <div className='memo'>
                {memos.length > 0 ? (
                    memos.map((memo, index) => (
                        <div key={index} className='memo-item'>
                            <ul>
                                <li>
                                    <Link to={`/memo/${memo.title}`}>
                                        <div className='front'>
                                            <input
                                                type='checkbox'
                                                checked={!!memo.checked}
                                                onChange={() => handleCheckboxChange(index)}
                                            />
                                            <div className='m_title'>{memo.title}</div>
                                        </div>
                                    </Link>
                                    <div className='m_time'>{memo.created_at}</div>
                                </li>
                            </ul>
                        </div>
                    ))
                ) : (
                    "No memos available"
                )}
            </div>
        </>
    );

    const renderMemoInput = () => (
        <>
            <div className='bar'>
                <Link to='/memo'>
                    <button className="w-btn w-btn-indigo" type="button">back</button>
                </Link>
                <button onClick={uploadMemo} className="w-btn w-btn-pink" type="button">upload</button>
            </div>
            <div className='title'>
                <input
                    className='text_title'
                    type='text'
                    placeholder='Title'
                    value={inputValue}
                    onChange={handleInputChange}
                />
                <textarea
                    className='content'
                    placeholder='Content'
                    value={textValue}
                    onChange={handleTextChange}
                />
            </div>
        </>
    );
    return (
        <S.Components>
            <div className='background'>
                {location.pathname === '/memo' ? renderMemoList() : renderMemoInput()}
                </div>
        </S.Components>
    );
}

export default Memo;
