import React, { useRef, useEffect } from 'react';
import { useSelector } from "react-redux";

const CanvasLadder = ({ imgRefs, inputRefs }) => {
  const { players } = useSelector((state) => state.data);
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    if (!ctx || !imgRefs.current || !inputRefs.current) return;

    // Calculate canvas size and step
    const ladderWidth = canvas.width;
    const ladderHeight = canvas.height;
    const numPlayers = players.length;
    const stepHeight = ladderHeight / (numPlayers + 1);
    
    // Clear previous drawings
    ctx.clearRect(0, 0, ladderWidth, ladderHeight);

    ctx.strokeStyle = '#000';
    ctx.lineWidth = 2;

    // Draw vertical ladder lines from image to input
    imgRefs.current.forEach((imgRef, index) => {
      const imgRect = imgRef.getBoundingClientRect();
      const inputRect = inputRefs.current[index].getBoundingClientRect();
      ctx.beginPath();
      ctx.moveTo(imgRect.right, imgRect.bottom);
      ctx.lineTo(inputRect.left, inputRect.bottom);
      ctx.stroke();
    });

    // Draw players
    ctx.fillStyle = '#f00';
    players.forEach((_, index) => {
      const imgRect = imgRefs.current[index].getBoundingClientRect();
      ctx.beginPath();
      ctx.arc(imgRect.right - 20, imgRect.bottom + (index + 1) * stepHeight, 10, 0, Math.PI * 2);
      ctx.fill();
    });

  }, [players, imgRefs, inputRefs]);

  return (
    <canvas
      ref={canvasRef}
      width={1600}
      height={200}
      style={{ border: '1px solid #000', position: 'absolute', top: 0, left: 0 }}
    />
  );
};

export default CanvasLadder;
