import { SET_IMGDATA,SET_PLAYER } from '../actions/types';
import dabin from '../../image/db.jpg'
import minki from '../../image/mk.jpg'
import soogon from '../../image/sg.jpg'
import sengyoun from '../../image/sy.jpg'
import everyone from '../../image/everyone.jpg'
import { images } from '../imageFile/Home';

const allData = [
    images
  ];

  const initialState = {
    data: allData.slice(0, 2),
    playerCount: 2,
    players: [],
  };

  const imgdataReducer = (state = initialState, action) => {
    switch (action.type) {
      case SET_IMGDATA:
        return { ...state, data: action.payload };
  
      case "INC_PLAYERS": {
        const newPlayerCount = state.playerCount + 1;
        return {
          ...state,
          playerCount: newPlayerCount,
          data: allData.slice(0, newPlayerCount), 
        };
      }
  
      case "DEC_PLAYERS": {
        const newPlayerCount = Math.max(2, state.playerCount - 1);
        return {
          ...state,
          playerCount: newPlayerCount,
          data: allData.slice(0, newPlayerCount),
        };
      }
  
      default:
        return state;
    }
  };

export default imgdataReducer;
