import { configureStore } from '@reduxjs/toolkit';
import memoReducer from './reducers/memoReducer'; // 메모 리듀서를 가져옵니다.

const store = configureStore({
  reducer: {
    memo: memoReducer, // 'memo' 상태를 memoReducer에 매핑합니다.
  },
});

export default store;
