import React ,{useState,useEffect}from 'react';
import * as S from './style';
import { useDispatch, useSelector } from 'react-redux';
import { setUserDetails, setLoading, setError, setSuccess } from '../redux/actions/UserAction';

function Members() {
    const dispatch = useDispatch();
    const { userDetails, isLoading, error, success } = useSelector(state => state.user);
    
    const handleChange = (e) => {
        const { name, value } = e.target;
        dispatch(setUserDetails(''));
    };
  return (
    <S.Component>
 <div className='members_body'>
 <div className='Member_input'>
 <input
  name="email"
  value={userDetails.email}
  type='text'
  placeholder="아이디"
  onChange={handleChange}
/>
<input
  name="password"
  value={userDetails.password}
  placeholder="비밀번호"
  onChange={handleChange}
/>
<input
  name="id"
  value={userDetails.id}
  placeholder="닉네임"
  onChange={handleChange}
/>
<input
  name="name"
  value={userDetails.name}
  placeholder="니 이름"
  onChange={handleChange}
/>
 <button className='login'>완료</button>
 </div>
 </div>
    </S.Component>
   
  )
}

export default Members
