import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';  

function DataInput({memos,title,handlesubmit}) {
    const [titles, setTitle] = useState(memos.title);
    const [content, setContent] = useState(memos.content);

    useEffect(() => {
        setTitle(memos.title);
        setContent(memos.content);
    }, [memos]);
    const handleUpdate = () => {
        handlesubmit({ ...memos, titles, content });
    };
    const memo = memos.find(memo => memo.title === title);
    if (!memo) {
        return <div>Memo not found</div>; // 해당 메모가 없을 때 처리
    }
  return (
    <>
     <div className='bar'>
                <Link to='/memo'>
                    <button className="w-btn w-btn-indigo" type="button">Back</button>
                </Link>
                <button className="w-btn w-btn-pink" onClick={handleUpdate} type="button">Update</button>
            </div>
            <div className='title'>
                
                    <input 
                    className='text_title'
                        type="text" 
                        value={title} 
                        onChange={(e) => setTitle(e.target.value)} 
                    />
              
               
                    <textarea 
                    className='content'
            placeholder='Content'
                        value={content} 
                        onChange={(e) => setContent(e.target.value)} 
                    />
             
            </div>
        </>
  )
}

export default DataInput
