// reducers/numberReducer.js
import { SET_NUMBER, RESET_NUMBER, DECREMENT_NUMBER } from '../actions/types';

const initialState = {
  number: 0,
  maxNumber: 10,
  minNumber: 0, // minNumber를 추가합니다.
};

const numberReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_NUMBER:
      if (action.payload > state.maxNumber) {
        return { ...state, number: state.maxNumber };
      }
      return { ...state, number: action.payload };
    case DECREMENT_NUMBER:
      if (action.payload < state.minNumber) {
        return { ...state, number: state.minNumber };
      }
      return { ...state, number: action.payload };
    case RESET_NUMBER:
      return { ...state, number: initialState.number };
    default:
      return state;
  }
};

export default numberReducer;
