import React, { useState, useRef, useEffect,useCallback } from 'react';

const Canvas = ({ data, imgRefs }) => {
  const canvasRef = useRef(null);
  const [width, setWidth] = useState(window.innerWidth -100);
  const [paths, setPaths] = useState([]);
  const [movingLine, setMovingLine] = useState(null);

  useEffect(() => {
    const updateCanvasSize = () => {
      setWidth(window.innerWidth); // 화면의 너비에 따라 canvas의 너비를 업데이트
    };

    window.addEventListener('resize', updateCanvasSize);
    updateCanvasSize();
    return () => window.removeEventListener('resize', updateCanvasSize);
  }, []);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext("2d");
    if (!ctx) return;

    const ladderHeight = 600;
    const player = data.length;
    const ladderY = 50;
    const ladderX = 100;
    const columnGap = (canvas.width - ladderX * 2) / (player - 1);

    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // Draw vertical lines
    const imgPositions = [];
    data.forEach((_, index) => {
      const img = imgRefs.current[index];
      if (img) {
        const rect = img.getBoundingClientRect();
        const canvasRect = canvas.getBoundingClientRect();
        const imgX = rect.left - canvasRect.left + img.width / 2;
        imgPositions.push(imgX);
        ctx.beginPath();
        ctx.moveTo(imgX, ladderY);
        ctx.lineTo(imgX, ladderY + ladderHeight);
        ctx.stroke();
      }
    });

    // Draw horizontal lines
    const minHorizontalLines = player - 1;
    const horizontalLines = minHorizontalLines + Math.ceil(Math.random() * 5);
    const newPaths = Array.from({ length: player }, () => []);

    for (let i = 0; i < horizontalLines; i++) {
      const x1 = imgPositions[i % imgPositions.length];
      const x2 = imgPositions[(i + 1) % imgPositions.length];
      let startY = ladderY + Math.random() * ladderHeight;

      ctx.beginPath();
      ctx.moveTo(x1, startY);
      ctx.lineTo(x2, startY);
      ctx.stroke();

      newPaths[imgPositions.indexOf(x1)].push({
        coord: { startX: x1, endX: x2, y: startY },
        connectedIndex: { vertical: imgPositions.indexOf(x2) }
      });
    }

    setPaths(newPaths);
  }, [data, imgRefs]);

  const showResult = useCallback((startPoint) => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext("2d");
    if (!ctx) return;

    ctx.lineWidth = 5;
    ctx.strokeStyle = "red";

    const userIndex = startPoint - 1;
    const path = paths[userIndex];

    if (path.length === 0) return;

    let prevX = imgRefs.current[userIndex].getBoundingClientRect().left - canvas.getBoundingClientRect().left + imgRefs.current[userIndex].width / 2;
    let prevY = 50;

    const animatePath = () => {
      if (movingLine) {
        requestAnimationFrame(animatePath);
      }

      ctx.clearRect(0, 0, canvas.width, canvas.height); // Clear previous path
      ctx.strokeStyle = "black";
      redrawLadder(); // Redraw the ladder

      let nextLine = path[0];
      let t = 0;

      while (nextLine) {
        ++t;
        if (t >= 1000) break; // Protect against infinite loops

        const { startX, endX, y } = nextLine.coord;

        // Draw vertical path
        ctx.beginPath();
        ctx.moveTo(prevX, prevY);
        ctx.lineTo(prevX, y);
        ctx.stroke();

        // Draw horizontal path
        ctx.beginPath();
        ctx.moveTo(startX, y);
        ctx.lineTo(endX, y);
        ctx.stroke();

        const nextLineIndex = paths[nextLine.connectedIndex.vertical].findIndex(
          ({ coord }) =>
            coord.startX === endX && coord.endX === startX && coord.y === y
        );

        const tempNextLine = paths[nextLine.connectedIndex.vertical]?.[nextLineIndex + 1];

        if (tempNextLine && nextLineIndex !== -1) {
          nextLine = tempNextLine;
          prevX = endX;
          prevY = y;
        } else {
          // Draw vertical line to the bottom of the canvas if no more horizontal lines
          ctx.beginPath();
          ctx.moveTo(endX, y);
          ctx.lineTo(endX, 50 + 600);
          ctx.stroke();
          break;
        }
      }

      setMovingLine(nextLine);
    };

    setMovingLine(null);
    animatePath();
  }, [paths, imgRefs, movingLine]);

  const redrawLadder = () => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext("2d");
    if (!ctx) return;

    const ladderHeight = 600;
    const player = data.length;
    const ladderY = 50;
    const ladderX = 100;
    const columnGap = (canvas.width - ladderX * 2) / (player - 1);

    ctx.strokeStyle = "black";

    // Redraw vertical lines
    data.forEach((_, index) => {
      const img = imgRefs.current[index];
      if (img) {
        const rect = img.getBoundingClientRect();
        const canvasRect = canvas.getBoundingClientRect();
        const imgX = rect.left - canvasRect.left + img.width / 2;
        ctx.beginPath();
        ctx.moveTo(imgX, ladderY);
        ctx.lineTo(imgX, ladderY + ladderHeight);
        ctx.stroke();
      }
    });

    // Redraw horizontal lines
    const minHorizontalLines = player - 1;
    const horizontalLines = minHorizontalLines + Math.ceil(Math.random() * 5);
    const imgPositions = [];

    data.forEach((_, index) => {
      const img = imgRefs.current[index];
      if (img) {
        const rect = img.getBoundingClientRect();
        const canvasRect = canvas.getBoundingClientRect();
        const imgX = rect.left - canvasRect.left + img.width / 2;
        imgPositions.push(imgX);
      }
    });

    for (let i = 0; i < horizontalLines; i++) {
      const x1 = imgPositions[i % imgPositions.length];
      const x2 = imgPositions[(i + 1) % imgPositions.length];
      let startY = ladderY + Math.random() * ladderHeight;

      ctx.beginPath();
      ctx.moveTo(x1, startY);
      ctx.lineTo(x2, startY);
      ctx.stroke();
    }
  };

  return (
    <canvas
      ref={canvasRef}
      width={width}
      height={600}
      onClick={(e) => {
        const rect = canvasRef.current.getBoundingClientRect();
        const x = e.clientX - rect.left;
        const userIndex = Math.floor((x - 100) / (width - 200) * (data.length - 1)) + 1;
        showResult(userIndex);
      }}
    />
  );
};

export default Canvas;
