import React, { useState, useRef, useEffect } from 'react';

const Canvas = ({ data, imgRefs }) => {
  const canvasRef = useRef(null);
  const [width, setWidth] = useState(window.innerWidth - 100);
  const [paths, setPaths] = useState([]);
  const [animatedPaths, setAnimatedPaths] = useState([]);
  const [animationFrameId, setAnimationFrameId] = useState(null);

  // 화면 크기 변경 시 캔버스 크기 업데이트
  useEffect(() => {
    const updateCanvasSize = () => {
      setWidth(window.innerWidth);
    };

    window.addEventListener('resize', updateCanvasSize);
    updateCanvasSize();
    return () => window.removeEventListener('resize', updateCanvasSize);
  }, []);

  // 수직선 및 수평선 그리기
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext("2d");
    if (!ctx) return;

    const ladderHeight = 600;
    const ladderY = 50;

    ctx.clearRect(0, 0, canvas.width, canvas.height);
    const imgPositions = [];
    data.forEach((playerData, index) => {
      const img = imgRefs.current[index];
      if (img) {
        const rect = img.getBoundingClientRect();
        const canvasRect = canvas.getBoundingClientRect();
        const imgX = rect.left - canvasRect.left + img.width / 2;
        imgPositions.push(imgX);
        ctx.beginPath();
        ctx.moveTo(imgX, 0);
        ctx.lineTo(imgX, ladderHeight);
        ctx.stroke();
      }
    });

    const minHorizontalLines = data.length - 1;
    const horizontalLines = minHorizontalLines + Math.ceil(Math.random() * 5);

    const newPaths = Array.from({ length: data.length }, () => []);
    const newAnimatedPaths = [];
    for (let i = 0; i < horizontalLines; i++) {
      const x1 = imgPositions[i % imgPositions.length];
      const x2 = imgPositions[(i + 1) % imgPositions.length];
      let startY = ladderY + Math.random() * ladderHeight;

      newPaths[imgPositions.indexOf(x1)].push({
        coord: { startX: x1, endX: x2, y: startY },
        connectedIndex: { vertical: imgPositions.indexOf(x2) }
      });

      // 애니메이션 경로 초기화
      newAnimatedPaths.push({
        coord: { startX: x1, endX: x2, y: startY },
        currentX: x1,
        animationProgress: 0
      });
    }

    setPaths(newPaths);
    setAnimatedPaths(newAnimatedPaths);
  }, [width, data, imgRefs]);

  // 애니메이션을 위한 useEffect
  useEffect(() => {
    const animate = () => {
      const canvas = canvasRef.current;
      if (!canvas) return;

      const ctx = canvas.getContext("2d");
      if (!ctx) return;

      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // 기본 경로 그리기
      ctx.strokeStyle = 'black'; // 기본 경로 색상
      paths.forEach((pathArray) => {
        pathArray.forEach((path) => {
          const { startX, endX, y } = path.coord;
          ctx.beginPath();
          ctx.moveTo(startX, y);
          ctx.lineTo(endX, y);
          ctx.stroke();
        });
      });

      // 애니메이션 경로 그리기
      ctx.strokeStyle = 'red'; // 애니메이션 경로 색상
      const updatedAnimatedPaths = animatedPaths.map((path) => {
        const { coord, currentX, animationProgress } = path;
        const animationSpeed = 2; // 애니메이션 속도 조절
        const newX = currentX + animationSpeed;
        const { endX, y } = coord;

        if (newX > endX) {
          return { ...path, currentX: endX, animationProgress: 1 }; // 애니메이션 완료
        }

        ctx.beginPath();
        ctx.moveTo(currentX, y);
        ctx.lineTo(newX, y);
        ctx.stroke();

        return {
          ...path,
          currentX: newX,
          animationProgress: animationProgress + animationSpeed / (endX - coord.startX)
        };
      });

      setAnimatedPaths(updatedAnimatedPaths);

      // 애니메이션 계속
      setAnimationFrameId(requestAnimationFrame(animate));
    };

    animate();
    
    return () => {
      if (animationFrameId) {
        cancelAnimationFrame(animationFrameId);
      }
    };
  }, [paths, animatedPaths, animationFrameId]);

  return (
    <canvas
      ref={canvasRef}
      width={width}
      height={600}
    />
  );
};

export default Canvas;
