import React ,{useState,useEffect}from 'react';
import * as S from './style';
import { useDispatch, useSelector, } from 'react-redux';
import { setUserDetails, setLoading, setError, setSuccess } from '../redux/actions/UserAction';
import {  useNavigate } from 'react-router-dom';
function Members() {
    const dispatch = useDispatch();
    const { userDetails, isLoading, error, success } = useSelector(state => state.user);
    const navigate = useNavigate();
    const handleChange = (e) => {
      const { name, value } = e.target;
      
      dispatch(setUserDetails({ ...userDetails, [name]: value }));
    };
    const uploadUser = async () => {
      if (inputValue && textValue) {
          const user_data = { title: inputValue, content: textValue, name: textValue, created_at: memoTime };
          console.log('Sending data:', JSON.stringify(user_data));
          try {
              const response = await fetch('http://localhost:5000/api/user', {
                  method: 'POST',
                  headers: {
                      'Content-Type': 'application/json',
                  },
                  body: JSON.stringify(user_data),
              });
  
              const responseData = await response.json();
              console.log('Response data:', responseData);
  
              if (response.ok) {
                  navigate('/login');
                  
                  setUserDetails([...userDetails, responseData]);
              } else {
                  console.error('Failed to upload user:', responseData.message);
                  alert(responseData.message);
              }
          } catch (error) {
              console.error('Error:', error);
          }
      } else {
          console.log('err');
      }
  };
  return (
    <S.Component>
 <div className='members_body'>
 <div className='Member_input'>
 <input
  name="email"
  value={userDetails.email}
   type='text'
  placeholder="아이디"
  onChange={handleChange}
/>
<input
  name="password"
  value={userDetails.password}
  placeholder="비밀번호"
  onChange={handleChange}
/>
<input
  name="id"
  value={userDetails.id}
  placeholder="닉네임"
  onChange={handleChange}
/>
<input
  name="name"
  value={userDetails.name}
  placeholder="니 이름"
  onChange={handleChange}
/>
 <button onClick={uploadUser} className='login'>완료</button>
 </div>
 </div>
    </S.Component>
   
  )
}

export default Members
