import { configureStore } from '@reduxjs/toolkit';
import rootReducer from './reducers/MemoReducer'; // rootReducer를 가져옵니다.

const store = configureStore({
  reducer: rootReducer, // 루트 리듀서를 설정합니다.
});

export default store;
