import React, { useState, useRef, useEffect } from 'react';

const Canvas = ({ data, imgRefs }) => {
  const canvasRef = useRef(null);
  const [width, setWidth] = useState(window.innerWidth - 100);
  const [animatedPath, setAnimatedPath] = useState(null);
  const [imgPositions, setImgPositions] = useState([]);
  const animationFrameId = useRef(null);

  useEffect(() => {
    const updateCanvasSize = () => {
      setWidth(window.innerWidth - 100);
    };

    window.addEventListener('resize', updateCanvasSize);
    updateCanvasSize();
    return () => window.removeEventListener('resize', updateCanvasSize);
  }, []);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const ladderHeight = 600;
    const ladderY = 50;

    ctx.clearRect(0, 0, canvas.width, canvas.height);

    const newImgPositions = [];
    data.forEach((playerData, index) => {
      const img = imgRefs.current[index];
      if (img) {
        const rect = img.getBoundingClientRect();
        const canvasRect = canvas.getBoundingClientRect();
        const imgX = rect.left - canvasRect.left + img.width / 2;
        newImgPositions.push(imgX);

        // Draw vertical line
        ctx.beginPath();
        ctx.moveTo(imgX, 0);
        ctx.lineTo(imgX, ladderHeight);
        ctx.stroke();
      }
    });

    setImgPositions(newImgPositions);

    if (newImgPositions.length > 1) {
      // Define a single animated path
      setAnimatedPath({
        currentX: newImgPositions[0],
        targetX: newImgPositions[1],
        currentY: 0,
        direction: 'down',
        stepSize: 5,
        horizontalStepSize: 5,
        endY: ladderHeight,
        completed: false,
        initialX: newImgPositions[0],
      });
    }
  }, [width, data, imgRefs.current]);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const colors = ['red'];

    const animate = () => {
      if (!animatedPath) return;

      const {
        currentX,
        currentY,
        direction,
        stepSize,
        horizontalStepSize,
        targetX,
        endY,
        initialX
      } = animatedPath;

      ctx.strokeStyle = colors[0];
      ctx.lineWidth = 3;

      if (direction === 'down') {
        ctx.beginPath();
        ctx.moveTo(currentX, currentY);
        const nextY = endY;
        animatedPath.currentY = Math.min(currentY + stepSize, nextY);
        ctx.lineTo(currentX, animatedPath.currentY);
        ctx.stroke();

        if (animatedPath.currentY >= nextY) {
          animatedPath.direction = 'horizontal';   
          // Set targetX to the next position
          animatedPath.targetX = imgPositions[1]; // Update to next position if there are more
        }
      } else if (direction === 'horizontal') {
        ctx.lineWidth = 3;
        ctx.beginPath();
        ctx.moveTo(currentX, animatedPath.currentY);

        // Draw horizontal line
        const nextX = animatedPath.currentX < targetX
          ? Math.min(currentX + horizontalStepSize, targetX)
          : Math.max(currentX - horizontalStepSize, targetX);

        animatedPath.currentX = nextX;
        ctx.lineTo(nextX, animatedPath.currentY);
        ctx.stroke();

        // Check if we reached the targetX
        if (animatedPath.currentX === targetX) {
          // Reverse direction
          animatedPath.direction = 'down';
          animatedPath.targetX = initialX; // Go back to the initial X
        }
      }

      if (animatedPath.currentY < endY) {
        animationFrameId.current = requestAnimationFrame(animate);
      } else {
        animatedPath.completed = true;
        cancelAnimationFrame(animationFrameId.current);
      }
    };

    animationFrameId.current = requestAnimationFrame(animate);

    return () => {
      cancelAnimationFrame(animationFrameId.current);
    };
  }, [animatedPath, imgPositions]);

  return (
    <canvas
      ref={canvasRef}
      width={width}
      height={600}
    />
  );
};

export default Canvas;
