import React, { useState, useRef, useEffect } from 'react';

const Canvas = ({ data, imgRefs }) => {
  const canvasRef = useRef(null);
  const [width, setWidth] = useState(window.innerWidth - 100);
  const [animatedPaths, setAnimatedPaths] = useState([]);
  const [startYValues, setStartValues] = useState([]);
  const [imgPositions, setImgPositions] = useState([]);
  const animationFrameId = useRef(null);

  useEffect(() => {
    const updateCanvasSize = () => {
      setWidth(window.innerWidth - 100);
    };

    window.addEventListener('resize', updateCanvasSize);
    updateCanvasSize();
    return () => window.removeEventListener('resize', updateCanvasSize);
  }, []);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const ladderHeight = 600;
    const ladderY = 50;

    ctx.clearRect(0, 0, canvas.width, canvas.height);

    const newImgPositions = [];
    data.forEach((playerData, index) => {
      const img = imgRefs.current[index];
      if (img) {
        const rect = img.getBoundingClientRect();
        const canvasRect = canvas.getBoundingClientRect();
        const imgX = rect.left - canvasRect.left + img.width / 2;
        newImgPositions.push(imgX);

        // Draw vertical line
        ctx.beginPath();
        ctx.moveTo(imgX, 0);
        ctx.lineTo(imgX, ladderHeight);
        ctx.stroke();
      }
    });

    setImgPositions(newImgPositions);
  
    const minHorizontalLines = data.length - 1;
    const horizontalLines = minHorizontalLines + Math.ceil(Math.random() * 5);

    const newAnimatedPaths = [];
    const newStartYValues = [];

    for (let i = 0; i < horizontalLines; i++) {
      const x1 = newImgPositions[i % newImgPositions.length];
      const x2 = newImgPositions[(i + 1) % newImgPositions.length];
      let startY = ladderY + Math.random() * ladderHeight;
      // Draw horizontal line
      ctx.beginPath();
      ctx.moveTo(x1, startY);
      ctx.lineTo(x2, startY);
      ctx.stroke();
      newStartYValues.push(startY);

      if (newAnimatedPaths.length < data.length) {
        newAnimatedPaths.push({
          currentX: Math.round(x1),
          targetX: Math.round(x2),
          y: Math.round(startY),
          currentY: 0,
          direction: 'down',
          horizontalStepSize: 5,
          stepSize: 5,
          endY: ladderHeight,
          completed: false,
          initialX: Math.round(x1),
        });
      }
    }
    setStartValues(newStartYValues);
    setAnimatedPaths(newAnimatedPaths);
  }, [width, data, imgRefs.current]);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const colors = ['red', 'blue', 'green', 'orange', 'pink'];

    const animate = () => {
      let allPathsCompleted = true;
      console.log(animatedPaths)
      setAnimatedPaths((paths) => {
        return paths.map((path, index) => {
          if (path.completed) return path; // 이미 완료된 애니메이션은 건너뛰기

          const { currentX, currentY, direction, stepSize, targetX, endY, initialX } = path;

          ctx.strokeStyle = colors[index % colors.length];
          ctx.lineWidth = 3;

          if (direction === 'down') {
            ctx.beginPath();
            ctx.moveTo(currentX, currentY);
            const nextY = startYValues.filter(yVal => yVal > currentY).sort((a, b) => a - b)[0] || endY;
            const updatedY = Math.min(currentY + stepSize, nextY);
            ctx.lineTo(currentX, updatedY);
            ctx.stroke();

            if (updatedY >= nextY) {
              return {
                ...path,
                currentY: updatedY,
                direction: 'horizontal'
              };
            } else {
              return {
                ...path,
                currentY: updatedY
              };
            }
          }

          if (direction === 'horizontal') {
            ctx.beginPath();
            ctx.moveTo(currentX, path.currentY);

            // Draw horizontal line
            const nextX = currentX < targetX
              ? Math.min(currentX + stepSize, targetX)
              : Math.max(currentX - stepSize, targetX);

            ctx.lineTo(nextX, path.currentY);
            ctx.stroke();

            if (nextX === targetX) {
              return {
                ...path,
                currentX: nextX,
                direction: 'down',
                targetX: initialX // 초기 X로 돌아감
              };
            } else {
              return {
                ...path,
                currentX: nextX
              };
            }
          }

          // 애니메이션이 완료된 상태로 업데이트
          if (path.currentY >= endY && path.currentX === initialX) {
            return {
              ...path,
              completed: true
            };
          } else {
            allPathsCompleted = false;
            return path;
          }
        });
      });

      if (!allPathsCompleted) {
        animationFrameId.current = requestAnimationFrame(animate);
      } else {
        cancelAnimationFrame(animationFrameId.current);
      }
    };

    animationFrameId.current = requestAnimationFrame(animate);

    return () => {
      cancelAnimationFrame(animationFrameId.current);
    };
  }, [animatedPaths, imgPositions]);

  return (
    <canvas
      ref={canvasRef}
      width={width}
      height={600}
    />
  );
};

export default Canvas;
