import React, { useState, useRef, useEffect } from 'react';

const Canvas = ({ data, imgRefs }) => {
  const canvasRef = useRef(null);
  const [width, setWidth] = useState(window.innerWidth - 100);
  const [paths, setPaths] = useState([]);
  const [animatedPaths, setAnimatedPaths] = useState([]);
  const animationFrameIdRef = useRef(null);

  useEffect(() => {
    const updateCanvasSize = () => {
      setWidth(window.innerWidth - 100); // 화면의 너비에 따라 canvas의 너비를 업데이트
    };

    window.addEventListener('resize', updateCanvasSize);
    updateCanvasSize();
    return () => window.removeEventListener('resize', updateCanvasSize);
  }, []);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const ladderHeight = 600;
    const ladderY = 50;

    ctx.clearRect(0, 0, canvas.width, canvas.height);
    const imgPositions = [];

    data.forEach((playerData, index) => {
      const img = imgRefs.current[index];
      if (img) {
        const rect = img.getBoundingClientRect();
        const canvasRect = canvas.getBoundingClientRect();
        const imgX = rect.left - canvasRect.left + img.width / 2;
        imgPositions.push(imgX);

        // 수직선 그리기
        ctx.beginPath();
        ctx.moveTo(imgX, 0);
        ctx.lineTo(imgX, ladderHeight);
        ctx.stroke();
      }
    });

    const minHorizontalLines = data.length - 1;
    const horizontalLines = minHorizontalLines + Math.ceil(Math.random() * 5);

    const newPaths = Array.from({ length: data.length }, () => []);
    const newAnimatedPaths = [];

    for (let i = 0; i < horizontalLines; i++) {
      const x1 = imgPositions[i % imgPositions.length];
      const x2 = imgPositions[(i + 1) % imgPositions.length];
      let startY = ladderY + Math.random() * ladderHeight;

      // 수평선 그리기
      ctx.beginPath();
      ctx.moveTo(x1, startY);
      ctx.lineTo(x2, startY);
      ctx.stroke();

      newPaths[imgPositions.indexOf(x1)].push({
        coord: { startX: x1, endX: x2, y: startY },
        connectedIndex: { vertical: imgPositions.indexOf(x2) }
      });

      if (newAnimatedPaths.length < data.length) {
        newAnimatedPaths.push({
          startX: x1,
        currentY: 0,
        targetX: x2,
        y: startY,
        direction: 'down', 
        horizontalProgress: 0,
        stepSize: 5 
        });
      }
    }

    setPaths(newPaths);
    setAnimatedPaths(newAnimatedPaths);
  }, [width, data, imgRefs]);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;
   
    const ctx = canvas.getContext('2d');
    if (!ctx) return;
    const colors = ['red', 'blue', 'green', 'orange', 'pink'];

    const animate = () => {
 

      // 모든 수직선 그리기 (애니메이션 적용)
      animatedPaths.forEach((path, index) => {
        const { startX, currentY, y, targetX, direction, horizontalProgress, stepSize } = path;

        ctx.strokeStyle = colors[index % colors.length];
        ctx.lineWidth = 2;

        // 수직 애니메이션
        if (direction === 'down') {
          ctx.beginPath();
          ctx.moveTo(startX, 0);
          ctx.lineTo(startX, currentY);
          ctx.stroke();

          if (currentY < y) {
            path.currentY += stepSize;
          } else {
            path.direction = 'horizontal'; // 수직 이동 완료 후 수평 이동 시작
          }
        }

        // 수평 애니메이션
        if (direction === 'horizontal') {
          ctx.beginPath();
          ctx.moveTo(startX, y);
          ctx.lineTo(startX + horizontalProgress, y);
          ctx.stroke();

          if (horizontalProgress < Math.abs(targetX - startX)) {
            path.horizontalProgress += stepSize;
            path.startX += targetX > startX ? stepSize : -stepSize;
          } else {
            path.direction = 'down'; // 수평 이동 완료 후 다시 수직 이동 시작
            path.horizontalProgress = 0;
          }
        }
      });

      // 수평선 다시 그리기 (수직선 뒤에 배치됨)
      paths.forEach((pathGroup) => {
        pathGroup.forEach((path) => {
          const { startX, endX, y } = path.coord;
          ctx.strokeStyle = 'black';
          ctx.beginPath();
          ctx.moveTo(startX, y);
          ctx.lineTo(endX, y);
          ctx.stroke();
        });
      });

      // 수평선 다시 그리기 (수직선 뒤에 배치됨)
      paths.forEach((pathGroup) => {
        pathGroup.forEach((path) => {
          const { startX, endX, y } = path.coord;
          ctx.strokeStyle = 'black';
          ctx.beginPath();
          ctx.moveTo(startX, y);
          ctx.lineTo(endX, y);
          ctx.stroke();
        });
      });

      animationFrameIdRef.current = requestAnimationFrame(animate);
    };

    animationFrameIdRef.current = requestAnimationFrame(animate);

    return () => cancelAnimationFrame(animationFrameIdRef.current);
  }, [animatedPaths, paths]);

  return (
    <canvas
      ref={canvasRef}
      width={width}
      height={600}
    />
  );
};

export default Canvas;
