import React, { useState, useRef, useEffect } from 'react';

const Canvas = ({ data, imgRefs }) => {
  const canvasRef = useRef(null);
  const [width, setWidth] = useState(window.innerWidth -100);
  const [paths, setPaths] = useState([]);
  const [positions, setPositions] = useState([]);
  const [pathIndices, setPathIndices] = useState([]);
//windowWidth
  useEffect(() => {
    const updateCanvasSize = () => {
      setWidth(window.innerWidth); // 화면의 너비에 따라 canvas의 너비를 업데이트
    };

    window.addEventListener('resize', updateCanvasSize);
    updateCanvasSize();
    return () => window.removeEventListener('resize', updateCanvasSize);
  }, []);
//canvas
   useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext("2d");
    if (!ctx) return;
  
    const ladderHeight = 600;
    const player =data.length
    const ladderY = 50;
    
    const minHorizontalLines = player - 1;
   

    ctx.clearRect(0, 0, canvas.width, canvas.height);
    const imgPositions = [];

    data.forEach((playerData, index) => {
      const img = imgRefs.current[index];
      console.log(img);
      if (img) {
        const rect = img.getBoundingClientRect();
        const canvasRect = canvas.getBoundingClientRect();
      
        const imgX = rect.left - canvasRect.left + img.width / 2;
        imgPositions.push(imgX)
        ctx.beginPath();
        ctx.moveTo(imgX, 0);
        ctx.lineTo(imgX, ladderHeight);
        ctx.stroke();
      }
    });
    console.log(imgPositions)
    setPaths([]);
    const newPaths = [];
    const horizontalLines = minHorizontalLines + Math.ceil(Math.random() * 5);
   
    for (let i = 0; i < horizontalLines; i++) {
      const x1 = imgPositions[i % imgPositions.length];
      const x2 = imgPositions[(i + 1) % imgPositions.length];
    
       let startY = ladderY + Math.random() * ladderHeight;
       newPaths.push({ x1, x2, y: startY });
       ctx.beginPath();
       ctx.moveTo(x1, startY);
       ctx.lineTo(x2, startY);
       ctx.stroke();
    }
    setPaths(newPaths);
  }, [width, data, imgRefs]);
  //ani
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext("2d");
    if (!ctx) return;

    const animateImages = () => {
      const images = imgRefs.current;
      const newPositions = images.map((data, index) => {
       const img = imgRefs.current[index];
        if (img) {
          const pathIndex = pathIndex[index] || 0;
          const path = paths[pathIndex];

          if (path) {
            const speed = 2;
            const currentPos = positions[index] || { x: path.x1, y: path.y };
            const nextPos = { x: path.x2, y: path.y };
            const dx = nextPos.x - currentPos.x;
            const dy = nextPos.y - currentPos.y;
            const dist = Math.sqrt(dx * dx + dy * dy);

            if (dist < speed) {
              const newPathIndex = (pathIndex + 1) % paths.length;
              setPathIndices(prev => {
                const newIndices = [...prev];
                newIndices[index] = newPathIndex;
                return newIndices;
              });
              return nextPos;
            } else {
              const ratio = speed / dist;
              return {
                x: currentPos.x + dx * ratio,
                y: currentPos.y + dy * ratio
              };
            }
          }
        }
        return { x: 0, y: 0 };
      });

      setPositions(newPositions);

      ctx.clearRect(0, 0, canvas.width, canvas.height);
      paths.forEach(({ x1, x2, y }) => {
        ctx.beginPath();
        ctx.moveTo(x1, y);
        ctx.lineTo(x2, y);
        ctx.stroke();
      });

      images.forEach((img, index) => {
        const { x, y } = newPositions[index] || { x: 0, y: 0 };
        ctx.drawImage(img, x - img.width / 2, y - img.height / 2);
      });

      requestAnimationFrame(animateImages);
    };

    animateImages();
  }, [imgRefs, paths, positions, pathIndices]);
  return (
    <canvas
      ref={canvasRef}
      width={width}
      height={600}
    />
  );
};

export default Canvas;
