import React, { useState, useRef, useEffect } from 'react';

const Canvas = ({ data, imgRefs }) => {
  const canvasRef = useRef(null);
  const [width, setWidth] = useState(window.innerWidth - 100);
  const [animatedPaths, setAnimatedPaths] = useState([]);
  const [startYValues, setStartValues] = useState([]);
  const [imgPositions, setImgPositions] = useState([]);
  const animationFrameId = useRef(null);

  useEffect(() => {
    const updateCanvasSize = () => {
      setWidth(window.innerWidth - 100);
    };

    window.addEventListener('resize', updateCanvasSize);
    updateCanvasSize();
    return () => window.removeEventListener('resize', updateCanvasSize);
  }, []);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const ladderHeight = 600;
    const ladderY = 50;

    ctx.clearRect(0, 0, canvas.width, canvas.height);

    const newImgPositions = [];
    data.forEach((playerData, index) => {
      const img = imgRefs.current[index];
      if (img) {
        const rect = img.getBoundingClientRect();
        const canvasRect = canvas.getBoundingClientRect();
        const imgX = rect.left - canvasRect.left + img.width / 2;
        newImgPositions.push(imgX);

        // Draw vertical line
        ctx.beginPath();
        ctx.moveTo(imgX, 0);
        ctx.lineTo(imgX, ladderHeight);
        ctx.stroke();
      }
    });

    setImgPositions(newImgPositions);

    const minHorizontalLines = data.length - 1;
    const horizontalLines = minHorizontalLines + Math.ceil(Math.random() * 5);

    const newAnimatedPaths = [];
    const newStartYValues = [];

    for (let i = 0; i < horizontalLines; i++) {
      const x1 = newImgPositions[i % newImgPositions.length];
      const x2 = newImgPositions[(i + 1) % newImgPositions.length];
      let startY = ladderY + Math.random() * ladderHeight;
      // Draw horizontal line
      ctx.beginPath();
      ctx.moveTo(x1, startY);
      ctx.lineTo(x2, startY);
      ctx.stroke();
      newStartYValues.push(startY);

      if (i === 1) { // 인덱스 1번 애니메이션만 추가
        newAnimatedPaths.push({
          startX: Math.round(x1),
          currentX: Math.round(x1),
          targetX: Math.round(x2),
          y: Math.round(startY),
          currentY: 0,
          direction: 'down',
          horizontalStepSize: 5,
          stepSize: 5,
          horizontalDirection: x2 > x1 ? 'right' : 'left',
          endY: ladderHeight,
          completed: false
        });
      }
    }
    setStartValues(newStartYValues);
    setAnimatedPaths(newAnimatedPaths);
  }, [width, data, imgRefs.current]);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const colors = ['red', 'blue', 'green', 'orange', 'pink'];

    const animate = () => {
      const pathIndex = 0; // 인덱스 1번을 처리하기 위해 인덱스를 0으로 설정
      if (animatedPaths[pathIndex]?.completed) {
        return; // 애니메이션이 완료된 경우 종료
      }

      const path = animatedPaths[pathIndex];
      const { currentX, currentY, direction, stepSize, horizontalStepSize, horizontalDirection, targetX, endY } = path;

      ctx.strokeStyle = colors[pathIndex % colors.length];
      ctx.lineWidth = 3;

      const nextY = startYValues.filter(yVal => yVal > currentY).sort((a, b) => a - b)[0] || endY;
      const roundedNextY = Math.round(nextY);

      if (direction === 'down') {
        ctx.beginPath();
        ctx.moveTo(currentX, currentY);
        path.currentY = Math.min(currentY + stepSize, roundedNextY);

        ctx.lineTo(currentX, path.currentY);
        ctx.stroke();

        if (path.currentY >= roundedNextY) {
          path.direction = 'horizontal';
          path.currentX = currentX;
          const currentXIndex = imgPositions.indexOf(currentX);
          const nextIndex = (currentXIndex + 1) % imgPositions.length;
          path.targetX = imgPositions[nextIndex];
          path.horizontalDirection = path.targetX > currentX ? 'right' : 'left';
        }
      } else if (direction === 'horizontal') {
        ctx.lineWidth = 3;
        ctx.beginPath();
        ctx.moveTo(currentX, path.currentY);
        const nextX = currentX + (horizontalDirection === 'right' ? horizontalStepSize : -horizontalStepSize);
        ctx.lineTo(nextX, path.currentY);
        ctx.stroke();

        if (horizontalDirection === 'right') {
          if (currentX < targetX) {
            path.currentX = Math.min(currentX + horizontalStepSize, targetX);
          } else if (currentX >= targetX) {
            path.direction = 'down';
            const nextY = startYValues.filter(yVal => yVal > path.currentY).sort((a, b) => a - b)[0] || endY;
            path.currentY = Math.round(nextY);
          }
        } else if (horizontalDirection === 'left') {
          if (currentX > targetX) {
            path.currentX = Math.max(currentX - horizontalStepSize, targetX);
          } else if (currentX <= targetX) {
            path.direction = 'down';
            const nextY = startYValues.filter(yVal => yVal > path.currentY).sort((a, b) => a - b)[0] || endY;
            path.currentY = Math.round(nextY);
          }
        }
      }

      if (path.currentY < endY || path.currentX < targetX || path.currentX > targetX) {
        animationFrameId.current = requestAnimationFrame(animate);
      } else {
        path.completed = true;
        cancelAnimationFrame(animationFrameId.current);
      }
    };

    animationFrameId.current = requestAnimationFrame(animate);

    return () => {
      cancelAnimationFrame(animationFrameId.current);
    };
  }, [animatedPaths, imgPositions]);

  return (
    <canvas
      ref={canvasRef}
      width={width}
      height={600}
    />
  );
};

export default Canvas;
