import { SET_NUMBER } from '../actions/types';

const initialState = {
  number: 0, // 초기 상태를 설정합니다.
};

const memoReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_MEMO:
      return { ...state, number: action.payload };
    default:
      return state;
  }
};

export default memoReducer;
