import React from 'react'
import Start from './Start'
import * as S from './style'
import { useSelector, useDispatch } from "react-redux";
import { setNumber,resetNumber } from '../redux/actions/NumberAction';

function Index() {
  const dispatch =useDispatch();
  const  {number,maxNumber,minNumber} = useSelector(state => state.number);


  const plus = (e) =>{
    if (number < maxNumber){
    dispatch(setNumber(number +1))
    }
  }
  const min = ()=>{
    if(number>minNumber ){
      dispatch(setNumber(number -1))
    }
  }
  const resetCounter = () => {
    dispatch(resetNumber());
  };
  return (
    <S.components>
<div className='body'>
    <Start number={number} plus={plus} miners={min}resetNumber={resetCounter} />
</div>
    </S.components>
    
  )
}

export default Index
