import React, { useState, useRef, useEffect } from 'react';

const Canvas = ({ data, imgRefs }) => {
  const canvasRef = useRef(null);
  const [width, setWidth] = useState(window.innerWidth - 100);
  const [animatedPaths, setAnimatedPaths] = useState([]);
  const animationFrameIdRef = useRef(null);

  useEffect(() => {
    const updateCanvasSize = () => {
      setWidth(window.innerWidth - 100); // 화면의 너비에 따라 canvas의 너비를 업데이트
    };

    window.addEventListener('resize', updateCanvasSize);
    updateCanvasSize();
    return () => window.removeEventListener('resize', updateCanvasSize);
  }, []);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const ladderHeight = 600;
    const ladderY = 50;

    ctx.clearRect(0, 0, canvas.width, canvas.height);
    const imgPositions = [];

    data.forEach((playerData, index) => {
      const img = imgRefs.current[index];
      if (img) {
        const rect = img.getBoundingClientRect();
        const canvasRect = canvas.getBoundingClientRect();
        const imgX = rect.left - canvasRect.left + img.width / 2;
        imgPositions.push(imgX);

        // 수직선 그리기
        ctx.beginPath();
        ctx.moveTo(imgX, 0);
        ctx.lineTo(imgX, ladderHeight);
        ctx.stroke();
      }
    });

    const minHorizontalLines = data.length - 1;
    const horizontalLines = minHorizontalLines + Math.ceil(Math.random() * 5);

    const newAnimatedPaths = [];

    for (let i = 0; i < horizontalLines; i++) {
      const x1 = imgPositions[i % imgPositions.length];
      const x2 = imgPositions[(i + 1) % imgPositions.length];
      let startY = ladderY + Math.random() * ladderHeight;

      // 수평선 그리기
      ctx.beginPath();
      ctx.moveTo(x1, startY);
      ctx.lineTo(x2, startY);
      ctx.stroke();
      console.log(x1)
      console.log(x2)
      if (newAnimatedPaths.length < data.length) {
        newAnimatedPaths.push({
          startX: Math.round(x1),
          currentY: 0,
          targetX: Math.round(x2),
          y: Math.round(startY),
          direction: 'down', // 수직 이동 시작
          horizontalStepSize:0.05,
          stepSize: 2 ,// 애니메이션 속도
        
        });
      }
    }

    setAnimatedPaths(newAnimatedPaths);
  }, [width, data, imgRefs]);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;
  
    const ctx = canvas.getContext('2d');
    if (!ctx) return;
  
    const colors = ['red', 'blue', 'green', 'orange', 'pink'];
  
    // 수평 이동을 위한 setInterval 설정
    const horizontalAnimationInterval = setInterval(() => {
      animatedPaths.forEach((path, index) => {
        const { startX, targetX, y, direction, horizontalStepSize } = path;
  
        if (direction === 'horizontal') {
          ctx.strokeStyle = colors[index % colors.length];
          ctx.lineWidth = 2;
  
          ctx.beginPath();
          ctx.moveTo(path.currentX, y);
          ctx.lineTo(path.currentX + horizontalStepSize, y);
          ctx.stroke();
  
          path.currentX += horizontalStepSize;
  
          if (path.currentX >= targetX) {
            path.currentX = targetX; // 목표 위치에 도달하면 위치 고정
            path.direction = 'down'; // 수평 이동 완료 후 다시 수직 이동
          }
        }
      });
    }, 16); // 16ms (약 60FPS)에 한 번씩 수평 애니메이션 업데이트
  
    // 수직 애니메이션을 위한 requestAnimationFrame 설정
    const verticalAnimation = () => {
      animatedPaths.forEach((path, index) => {
        const { startX, currentY, y, direction, stepSize } = path;
  
        if (direction === 'down') {
          ctx.strokeStyle = colors[index % colors.length];
          ctx.lineWidth = 2;
  
          ctx.beginPath();
          ctx.moveTo(startX, 0);
          ctx.lineTo(startX, currentY);
          ctx.stroke();
  
          if (currentY < y) {
            path.currentY = Math.min(currentY + stepSize, y);
          } else if (currentY === y) {
            path.direction = 'horizontal'; // 수직 이동 완료 후 수평 이동 준비
            path.currentX = startX; // 현재 위치를 수평 이동의 시작 위치로 설정
          }
        }
      });
  
      animationFrameIdRef.current = requestAnimationFrame(verticalAnimation);
    };
  
    animationFrameIdRef.current = requestAnimationFrame(verticalAnimation);
  
    return () => {
      cancelAnimationFrame(animationFrameIdRef.current);
      clearInterval(horizontalAnimationInterval); // 수평 애니메이션 종료
    };
  }, [animatedPaths]);
  
  return (
    <canvas
      ref={canvasRef}
      width={width}
      height={600}
    />
  );
};

export default Canvas;
