import React, { useState, useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from "react-redux";
import { setMemo } from "../redux/actions/memoActions"; // 액션 생성자
import * as S from './style';

function Memo() {
    const location = useLocation();
    const navigate = useNavigate();
    const [inputValue, setInputValue] = useState(''); // input -> inputValue로 변경
    const [textValue, setTextValue] = useState('');   // text -> textValue로 변경
    const [localMemos, setLocalMemos] = useState([]); // 상태 변수 이름 변경

    const dispatch = useDispatch();
    const memos = useSelector(state => state.memo.memo); // 상태 가져오기

    // 메모 상태 업데이트 함수
    const updateMemos = (newMemos) => {
        dispatch(setMemo(newMemos));
    };

    const handleInputChange = (e) => {
        setInputValue(e.target.value);  
    };

    const handleTextChange = (e) => {
        setTextValue(e.target.value);  
    };

    const handleCheckboxChange = (index) => {
        setLocalMemos((prevMemos) =>
            prevMemos.map((memo, i) =>
                i === index ? { ...memo, checked: !memo.checked } : memo
            )
        );
    };

    const uploadMemo = async () => {
        if (inputValue && textValue) {
            const memoTime = new Date().toLocaleString();
            const newMemo = { title: inputValue, content: textValue, name: textValue, created_at: memoTime };
            console.log('Sending data:', JSON.stringify(newMemo)); 
            try {
                const response = await fetch('http://localhost:5000/api/notes', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify(newMemo),
                });

                if (response.ok) {
                    const result = await response.json();
                    console.log(result);
                    setInputValue('');
                    setTextValue('');
                    navigate('/memo');
                    updateMemos([...memos, result]); // 상태 업데이트
                } else {
                    console.error('Failed to upload memo');
                }
            } catch (error) {
                console.error('Error:', error);
            }
        } else {
            console.log('err');
        }
    };

    const deleteMemos = async () => {
        const idsToDelete = localMemos.filter(memo => memo.checked).map(memo => memo.id);

        for (const id of idsToDelete) {
            try {
                const response = await fetch(`http://localhost:5000/api/notes/${id}`, {
                    method: 'DELETE',
                });

                if (response.ok) {
                    console.log(`Deleted memo with id ${id}`);
                } else {
                    console.error(`Failed to delete memo with id ${id}`);
                }
            } catch (error) {
                console.error('Error:', error);
            }
        }

        setLocalMemos(localMemos.filter(memo => !memo.checked)); // 삭제된 메모를 제외한 나머지
    };

    useEffect(() => {
        const saved = localStorage.getItem("name");
        if (saved) {
            setLocalMemos(JSON.parse(saved));
        }
    }, []);

    useEffect(() => {
        localStorage.setItem("name", JSON.stringify(localMemos));
    }, [localMemos]);

    return (
        <S.Components>
            <div className='background'>
                {location.pathname === '/memo' ? (
                    <>
                        <div className='bar'>
                            <div className='left'>
                                <Link to={'/memo/input'}>
                                    <button className="w-btn w-btn-indigo" type="button">
                                        memo
                                    </button>
                                </Link>
                            </div>
                            <div className='right'>
                                <div className='s'>
                                    <button onClick={deleteMemos} className="w-btn w-btn-pink" type="button">
                                        Delete
                                    </button>
                                </div>
                            </div>
                        </div>
                        <div className='memo'>
                            {localMemos.length > 0 ? (
                                localMemos.map((memo, index) => (
                                    <div key={index} className='memo-item'>
                                        <ul>
                                            <li>
                                                <div className='front'>
                                                    <input 
                                                        type='checkbox' 
                                                        checked={memo.checked} 
                                                        onChange={() => handleCheckboxChange(index)} 
                                                    />
                                                    <div className='m_title'>{memo.title}</div>
                                                </div>
                                                <div className='m_time'>{memo.time}</div>
                                            </li>
                                        </ul> 
                                    </div>
                                ))
                            ) : (
                                "No memos available"
                            )}
                        </div>
                    </>
                ) : (
                    <>
                        <div className='bar'>
                            <div className='left'>
                                <Link to={'/memo'}>
                                    <button className="w-btn w-btn-indigo" type="button">
                                        back
                                    </button>
                                </Link>
                            </div>
                            <div className='right'>
                                <div className='a'>
                                    <button className="w-btn w-btn-pink" type="button" onClick={uploadMemo}>
                                        upload
                                    </button>
                                </div>
                            </div>
                        </div>
                        <div className='title'>
                            <input
                                className='text_title'
                                type='text'
                                placeholder="Title"
                                alt='Title'
                                value={inputValue}
                                onChange={handleInputChange}
                            />
                            <textarea
                                className='content'
                                placeholder='Content'
                                value={textValue}
                                onChange={handleTextChange}
                            />
                            <div className='upload' type='button' onClick={uploadMemo}>
                                Upload
                            </div>
                        </div>
                    </>
                )}
            </div>
        </S.Components>
    );
}

export default Memo;
