import './App.css';
import React ,{useEffect}from 'react';
import Naver from './Navbar/index';
import Home from './Home/index';
import Memo from './Memo';
import Loading from './Loading';
import { Routes, Route } from 'react-router-dom';
import {  setUserDetails,setLoading,setError } from './redux/actions/UserAction';
import { useDispatch, useSelector } from 'react-redux';
import Login from './Login';
import Members from './members';


function App() {
  const dispatch = useDispatch();
  const { isLoading,login } = useSelector(state => state.user) ;
  useEffect(() => {
    // 로그인 상태가 true일 때만 호출
    if (login) {
      fetchUserDetails(); // fetchUserDetails 호출
    }
  }, [login]); // 로그인 상태가 변경될 때 호출

  const fetchUserDetails = async () => {
    localStorage.setItem('token', data.token); // 로컬 스토리지에서 토큰 가져오기

    if (!token) {
      console.error('No token found in local storage');
      return;
    }

    dispatch(setLoading(true));
    dispatch(setError(null));

    try {
      const response = await fetch('http://localhost:5000/api/login/protected', { // 요청 URL 확인
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        console.error('Failed to fetch protected data:', errorData.message || 'Unknown error');
        dispatch(setError(errorData.message || 'Failed to fetch data'));
        return;
      }

      const data = await response.json();
      dispatch(setUserDetails(data.user));
      console.log('User details:', data.user);
    } catch (err) {
      console.error('Error fetching protected data:', err);
      dispatch(setError(err.message || 'An error occurred'));
    } finally {
      dispatch(setLoading(false));
    }
  };

  return (
    <div className='header'>
      <Naver />

      <div className='body'>

        {isLoading  ?<Loading/>: null} 
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/memo/*" element={<Memo />} />
          <Route path="/login" element={<Login/>}/>
          <Route path='/login/members' element={<Members/>}/>
        </Routes>
      </div>
    </div>
  );
}

export default App;
