import React, { useState, useRef, useEffect } from 'react';

const Canvas = ({ data, imgRefs }) => {
  const canvasRef = useRef(null);
  const [width, setWidth] = useState(window.innerWidth - 100);
  const [animatedPaths, setAnimatedPaths] = useState([]);
  const intervalIdRef = useRef(null);

  useEffect(() => {
    const updateCanvasSize = () => {
      setWidth(window.innerWidth - 100); // 화면의 너비에 따라 canvas의 너비를 업데이트
    };

    window.addEventListener('resize', updateCanvasSize);
    updateCanvasSize();
    return () => window.removeEventListener('resize', updateCanvasSize);
  }, []);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const ladderHeight = 600;
    const ladderY = 50;

    ctx.clearRect(0, 0, canvas.width, canvas.height);
    const imgPositions = [];

    data.forEach((playerData, index) => {
      const img = imgRefs.current[index];
      if (img) {
        const rect = img.getBoundingClientRect();
        const canvasRect = canvas.getBoundingClientRect();
        const imgX = rect.left - canvasRect.left + img.width / 2;
        imgPositions.push(imgX);

        // 수직선 그리기
        ctx.beginPath();
        ctx.moveTo(imgX, 0);
        ctx.lineTo(imgX, ladderHeight);
        ctx.stroke();
      }
    });

    const minHorizontalLines = data.length - 1;
    const horizontalLines = minHorizontalLines + Math.ceil(Math.random() * 5);

    const newAnimatedPaths = [];

    for (let i = 0; i < horizontalLines; i++) {
      const x1 = imgPositions[i % imgPositions.length];
      const x2 = imgPositions[(i + 1) % imgPositions.length];
      let startY = ladderY + Math.random() * ladderHeight;

      // 수평선 그리기
      ctx.beginPath();
      ctx.moveTo(x1, startY);
      ctx.lineTo(x2, startY);
      ctx.stroke();
  
      if (newAnimatedPaths.length < data.length) {
        newAnimatedPaths.push({
          currentX: Math.round(x1),
          currentY: 0,
          targetX: Math.round(x2),
          y: Math.round(startY),
          direction: 'down', // 수직 이동 시작
          horizontalStepSize: 2, // 수평 애니메이션 속도
          stepSize: 2, // 수직 애니메이션 속도
          horizontalDirection: x2 > x1 ? 'right' : 'left' // 수평 이동 방향 설정
        });
      }
    }

    setAnimatedPaths(newAnimatedPaths);
  }, [width, data, imgRefs]);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const colors = ['red', 'blue', 'green', 'orange', 'pink'];

    // 애니메이션 지속 시간 (5초)
    const duration = 5000; // 5초
    const intervalTime = 16; // 약 60fps

    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      animatedPaths.forEach((path, index) => {
        const { startX,  targetX, y, direction, horizontalDirection } = path;

        ctx.strokeStyle = colors[index % colors.length];
        ctx.lineWidth = 2;

        if (direction === 'down') {
          // 수직 애니메이션
          ctx.beginPath();
          ctx.moveTo(startX, 0);
          ctx.lineTo(startX, path.currentY);
          ctx.stroke();

          const verticalDistance = y;
          const verticalStepSize = verticalDistance / (duration / intervalTime);

          if (path.currentY < y) {
            path.currentY = Math.min(path.currentY + verticalStepSize, y);
          } else if (path.currentY === y) {
            path.direction = 'horizontal'; // 수직 이동 완료 후 수평 이동 준비
            path.currentX = startX; // 현재 위치를 수평 이동의 시작 위치로 설정
          }
        }

        if (direction === 'horizontal') {
          // 수평 애니메이션
          ctx.beginPath();
          ctx.moveTo(path.currentX, y);
          ctx.lineTo(path.currentX + (horizontalDirection === 'right' ? 1 : -1), y);
          ctx.stroke();

          const horizontalDistance = Math.abs(targetX - path.currentX);
          const horizontalStepSize = horizontalDistance / (duration / intervalTime);

          if (horizontalDirection === 'right') {
            if (path.currentX < targetX) {
              path.currentX = Math.min(path.currentX + horizontalStepSize, targetX);
            } else if (path.currentX >= targetX) {
              path.currentX = targetX; // 목표 위치에 도달하면 위치 고정
              path.direction = 'down'; // 수평 이동 완료 후 다시 수직 이동
            }
          } else if (horizontalDirection === 'left') {
            if (path.currentX > targetX) {
              path.currentX = Math.max(path.currentX - horizontalStepSize, targetX);
            } else if (path.currentX <= targetX) {
              path.currentX = targetX; // 목표 위치에 도달하면 위치 고정
              path.direction = 'down'; // 수평 이동 완료 후 다시 수직 이동
            }
          }
        }
      });
    };

    intervalIdRef.current = setInterval(animate, intervalTime);

    return () => {
      clearInterval(intervalIdRef.current); // clean up
    };
  }, [animatedPaths]);

  return (
    <canvas
      ref={canvasRef}
      width={width}
      height={600}
    />
  );
};

export default Canvas;
