import styled from "styled-components";

export const Component = styled.div`
display: flex;
justify-content: center;
a{
    font-size: 20px;
    color: #fff;
    text-decoration-line: none;
}
input{
border: 1px solid grey;
    border-radius: 5px;
    outline: none;
    background-color: #f5f5f5;
    color: #463f3a;
    margin: 0 2px 10px;
    flex-grow: .5;
    font-family: "Jua", sans-serif;
    font-size: 18px;
    font-weight: 900;
    padding: 0px 5px;
    width: 98%;
}
`
