import React, { useState } from 'react';
import { useSelector } from "react-redux";
import Canvas from './Canvas';

function Game({ players }) {
  const { data } = useSelector((state) => state.data);
  const [text, setText] = useState('');

  return (
    <div className='parent_body' style={{ position: 'relative' }}>
      / {/* Canvas를 데이터 리스트 위에 위치시킴 */}
      <div className='data_body' style={{ position: 'absolute', top: 0, left: 0, right: 0, bottom: 0 }}>
        {data.map((imgs, index) => (
          <div className='parent' key={index} style={{ position: 'relative', marginBottom: '20px' }}>
            <img src={imgs.src} alt={imgs.alt} style={{ display: 'block' }} />
            <input
              value={text}
              type='text'
              onChange={(e) => setText(e.target.value)}
              style={{ position: 'absolute', top: '100%', left: '0', transform: 'translateY(-50%)' }}
            />
          </div>
        ))}
      </div>
    </div>
  );
}

export default Game;
