import React, { useRef, useEffect } from 'react';

const Canvas = ({ data, imgRefs }) => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    if (!ctx) return;

    const ladderWidth = canvas.width;
    const ladderHeight = canvas.height;
    const numPlayers = data.length;
    const stepHeight = ladderHeight / (numPlayers + 1); // Height of each step

    
    ctx.clearRect(0, 0, ladderWidth, ladderHeight);

    // Draw vertical lines centered on images
    ctx.strokeStyle = 'red'; // Line color
    ctx.lineWidth = 2; // Line width

    data.forEach((playerData, index) => {
      const img = imgRefs.current[index];

      if (img) {
        // Calculate the center of the image
        const imgX = img.offsetLeft + img.width / 6;

        // Draw vertical line centered on the image
        ctx.beginPath();
        ctx.moveTo(imgX, 0); // Start at the top of the canvas
        ctx.lineTo(imgX, ladderHeight); // End at the bottom of the canvas
        ctx.stroke();
      }
    });

  
    ctx.strokeStyle = '#000'; 
    ctx.lineWidth = 2; 

    const numLines = Math.floor(Math.random() * 5) + 1; // Random number between 1 and 5

    // Randomly choose `numLines` horizontal line positions
    const lineYPositions = [];
    while (lineYPositions.length < numLines) {
      const randomY = (Math.floor(Math.random() * (numPlayers))) * stepHeight + stepHeight; // Ensure it starts at least one step down
      if (!lineYPositions.includes(randomY)) {
        lineYPositions.push(randomY);
      }
    }

    // Draw the horizontal lines
    lineYPositions.forEach(lineY => {
      data.forEach((_, index) => {
        const img = imgRefs.current[index];
        if (img) {
          // Calculate the center of the image
          const imgX = img.offsetLeft + img.width / 6;

          // Draw the horizontal line
          ctx.beginPath();
          ctx.moveTo(imgX, lineY); // Start at the X coordinate of the image
          ctx.lineTo(ladderWidth, lineY); // End at the end of the canvas width
          ctx.stroke();
        }
      });
    });
  }, [data, imgRefs]);

  return (
    <canvas
      ref={canvasRef}
      width={1600}
      height={400}
    />
  );
};

export default Canvas;
