import React,{useState} from 'react'
import * as S from './style'
import { useDispatch, useSelector } from 'react-redux';
import { setUserDetails, setLoading, setError, setSuccess } from '../redux/actions/UserAction';
import { Link,  useNavigate } from 'react-router-dom';

function Login() {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { userDetails } = useSelector(state => state.user);
  
  const handleChange = (e) => {
    const { name, value } = e.target;
    dispatch(setUserDetails({ [name]: value }));
};
  const login = () => {
    if(userDetails){
      navigate('/')
    }
   
  }
  return (
    <S.Component>
    <div className='Login_body'>
     
        <div className='Login_input'>
       <input value={userDetails.email} onChange={handleChange} placeholder='아이디' /> 
       <input value={userDetails.password} onChange={handleChange} placeholder='비밀번호'/>
    
        <button className='login'>로그인</button>
        <div className='joins'>
            <Link to={'/login/members'}><div className='join_members'>회원가입</div></Link>
            <div className='lookig_for'>비밀번호 찾기</div>
        </div>
        </div>

    </div>
    </S.Component>
  )
}

export default Login
