import React,{useState} from 'react'
import * as S from './style'
import { useDispatch, useSelector } from 'react-redux';
import { setUserDetails, setLoading, setError, setSuccess } from '../redux/actions/UserAction';
import { Link,  useNavigate } from 'react-router-dom';

function Login() {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { userDetails } = useSelector(state => state.user);
  
  const handleChange = (e) => {
    const { name, value } = e.target;
    dispatch(setUserDetails({ [name]: value }));
};
  const login = (err) => {
    dispatch(setLoading(true));
      dispatch(setError(null));
    if(userDetails){
      navigate('/')
      dispatch(setLoading(false));
      dispatch(setSuccess(true));
    }else{
      dispatch(setError(err.message));
    }
   
  }
  return (
    <S.Component>
    <div className='Login_body'>
     
        <div className='Login_input'>
        <input
  name="email"
  value={userDetails.email}
   type='text'
  placeholder="아이디"
  onChange={handleChange}
/>
<input
  name="password"
  value={userDetails.password}
   type='password'
  placeholder="비밀번호"
  onChange={handleChange}
/>
    
        <button className='login'>로그인</button>
        <div className='joins'>
            <Link to={'/login/members'}><div onClick={login} className='join_members'>회원가입</div></Link>
            <div className='lookig_for'>비밀번호 찾기</div>
        </div>
        </div>

    </div>
    </S.Component>
  )
}

export default Login
