import styled from "styled-components";

export const components = styled.div`
    display: flex;
    justify-content: center;
.body {
  background-color: #fff;
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
 
}
.Start {
  font-size: 69px;
  display: flex;
  justify-content: center;
  gap: 21px;
  margin-bottom: 34px;
}

.plus {
  font-size: 40px;
  margin-left: 40px;
}
.miners {
  font-size: 40px;
  margin-right: 40px;
}
.game {
  width: 100%;
}
.parent_body {
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100%;
       height: 1000px;
}

.data_container {

    display: flex;
      flex-direction: column;
   justify-content: center;
     width: 95%;
    height: 80%;
}

.item {

    display: flex;
    flex-direction: column;
   
}

.image {
  width: 96px;
    height: auto;
}

.input {
    width: 96px;
  
    padding: 5px;
}


.contetnt_1{
display:flex;
    justify-content: space-between;
}
.contetnt{
display:flex;
justify-content:center;
witdh:100%
}
`
