import React, { useRef, useEffect } from 'react';

const Canvas = ({ data, imgRefs }) => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    if (!ctx) return;

    const ladderWidth = canvas.width;
    const ladderHeight = canvas.height;
    const numPlayers = data.length;
    const stepHeight = ladderHeight / (numPlayers + 1); // Height of each step

    
    ctx.clearRect(0, 0, ladderWidth, ladderHeight);

    // Draw vertical lines centered on images
    ctx.strokeStyle = 'red'; // Line color
    ctx.lineWidth = 2; // Line width

    data.forEach((playerData, index) => {
      const img = imgRefs.current[index];

      if (img) {
        // Calculate the center of the image
        const imgX = img.offsetLeft + img.width / 6;

        // Draw vertical line centered on the image
        ctx.beginPath();
        ctx.moveTo(imgX, 0); // Start at the top of the canvas
        ctx.lineTo(imgX, ladderHeight); // End at the bottom of the canvas
        ctx.stroke();
      }
    });

  
    ctx.strokeStyle = '#000'; 
    ctx.lineWidth = 2; 

    for (let i = 0; i + 2< numPlayers; i++) {
      const lineY = (i + 1) * stepHeight;
     
      if (Math.random() > 0.5){
      data.forEach((_, index) => {
        const img = imgRefs.current[index];
        if (img) {  
      
          const imgX = img.offsetLeft + img.width / 6;

       
          ctx.beginPath();
          ctx.moveTo(imgX, lineY); 
          ctx.lineTo(ladderHeight, lineY);
          ctx.stroke();
        }
      }
    );
    }
  }
  }, [data, imgRefs]);

  return (
    <canvas
      ref={canvasRef}
      width={1600}
      height={400}
    />
  );
};

export default Canvas;
