import React, { useState, useRef, useEffect } from 'react';

const Canvas = ({ data, imgRefs }) => {
  const canvasRef = useRef(null);
  const [width, setWidth] = useState(0);
  const [lines, setLines] = useState([]); 
  const [distance,setDistance] = useState(0); 

  useEffect(() => {
    const updateCanvasSize = () => {
      const containerWidth = document.querySelector('.contetnt').offsetWidth;
      setWidth(containerWidth);
    };

    window.addEventListener('resize', updateCanvasSize);
    updateCanvasSize();

    return () => window.removeEventListener('resize', updateCanvasSize);
  }, []);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    if (!ctx) return;

    const ladderWidth = canvas.width;
    const ladderHeight = canvas.height;
    const numPlayers = data.length;
    const stepHeight = ladderHeight / (numPlayers + 3);

    ctx.clearRect(0, 0, ladderWidth, ladderHeight);

    ctx.strokeStyle = 'black';
    ctx.lineWidth = 5;

    let firstX = null;
    let secondX = null;

    // 수직선 그리기
    data.forEach((playerData, index) => {
      const img = imgRefs.current[index];
      if (img) {
        const imgX = img.offsetLeft + img.width / 6; // 상대적인 X 위치
        if (firstX === null) {
          firstX = imgX; // 첫 번째 수직선의 X 좌표 저장
        } else if (secondX === null) {
          secondX = imgX; // 두 번째 수직선의 X 좌표 저장
        }
        ctx.beginPath();
        ctx.moveTo(imgX, 0);
        ctx.lineTo(imgX, ladderHeight);
        ctx.stroke();
      }
    });

    if (firstX !== null && secondX !== null) {
      setDistance(Math.abs(secondX - firstX)); // 첫 번째와 두 번째 수직선 사이의 거리 계산
    }
    console.log(distance)
    ctx.strokeStyle = '#000';
    ctx.lineWidth = 5;

    lines.forEach((line) => {
      ctx.beginPath();
      ctx.moveTo(line.x * (ladderWidth / width), line.y);
      ctx.lineTo(ladderWidth, line.y);
      ctx.stroke();
    });

  }, [data, imgRefs, width, lines]);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const numPlayers = data.length;
    const ladderHeight = 600;
    const stepHeight = ladderHeight / (numPlayers + 3);

    const newLines = [];

    for (let i = 0; i < numPlayers + 4; i++) {
      const lineY = Math.min((i + 1) * stepHeight, 580);
      if (Math.random() > 0.5) {
        
        data.forEach((_, index) => {
          
          const img = imgRefs.current[index];
          if (img) {
            const imgX = img.offsetLeft + img.width /6 ;
            const nextX = imgX + distance;
              console.log(nextX)
            ctx.beginPath();
            ctx.moveTo(imgX, lineY);
            ctx.lineTo(nextX, lineY);
            ctx.stroke(); 
          }
        });
      }
    }
    console.log(newLines)
    setLines(newLines);
  }, [data, imgRefs]);

  return (
    <canvas
      ref={canvasRef}
      width={width}
      height={600}
    />
  );
};

export default Canvas;
