import { configureStore } from '@reduxjs/toolkit'
import memoReducer from './reducers/MemoReducer'; // memoReducer를 가져옵니다.

const rootReducer = {
  memo: memoReducer, // 메모 리듀서를 'memo' 상태에 매핑합니다.
};

export default rootReducer;
