import React, { useRef, useEffect } from 'react';
import { useSelector } from "react-redux";

const   CanvasLadder = () => {
  const { data } = useSelector((state) => state.data);
  const canvasRef = useRef(null);
  console.log(data)
  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    if (!ctx) return;

    const ladderWidth = canvas.width;
    const ladderHeight = canvas.height;
    const numPlayers = data.length;
    const stepHeight = ladderHeight / (numPlayers + 1);
    const stepWidth = ladderWidth / (numPlayers + 1);

    // Clear the canvas before drawing
    ctx.clearRect(0, 0, ladderWidth, ladderHeight);

    // Draw vertical ladder lines
    ctx.strokeStyle = '#000';
    ctx.lineWidth = 2;

    for (let i = 0; i <= numPlayers; i++) {
      ctx.beginPath();
      ctx.moveTo(i * stepWidth, 0);
      ctx.lineTo(i * stepWidth, ladderHeight);
      ctx.stroke();
    }

    // Assume players are drawn by the img elements, not by canvas
    // However, we can still use the player data for any additional canvas effects
    players.forEach((player, index) => {
      const img = imgRefs.current[index];
      if (img) {
        // Here, you can adjust the img style or add effects on canvas
        // Or use the img position to draw additional elements on the canvas
        const imgX = img.offsetLeft + img.width / 2;
        const imgY = img.offsetTop + img.height / 2;

        // Example: Draw a circle around the image on the canvas
        ctx.beginPath();
        ctx.arc(imgX, imgY, 20, 0, Math.PI * 2);
        ctx.strokeStyle = 'red';
        ctx.stroke();
      }
    });

  }, [players, imgRefs]);


  return (
    <canvas
      ref={canvasRef}
      width={1600}
      height={400}
      style={{ border: '1px solid #000' }}
    />
  );
};

export default CanvasLadder;
