import React, { useRef, useEffect } from 'react';
import { useSelector } from "react-redux";

const CanvasLadder = ({players}) => {

  const canvasRef = useRef(null);
  console.log(players)
  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    if (!ctx) return; // Ensure the context is available

    const ladderWidth = canvas.width;
    const ladderHeight = canvas.height;
    const numPlayers = playerCount.length;
    const stepHeight = ladderHeight / (numPlayers + 1);
    const stepWidth = ladderWidth / (numPlayers + 1);
    const ladderLines = [];
    
    console.log("Canvas dimensions:", ladderWidth, ladderHeight);
    console.log("Number of players:", numPlayers);
    console.log("Step height:", stepHeight);
    console.log("Step width:", stepWidth);

    // Generate random horizontal lines
    for (let i = 0; i < numPlayers - 1; i++) {
      for (let j = 1; j < numPlayers; j++) {
        if (Math.random() > 0.5) { // 50% chance to add a horizontal line
          ladderLines.push({
            start: { x: j * stepWidth, y: i * stepHeight },
            end: { x: (j + 1) * stepWidth, y: i * stepHeight }
          });
        }
      }
    }

    console.log("Generated ladder lines:", ladderLines);

    // Clear the canvas before drawing
    ctx.clearRect(0, 0, ladderWidth, ladderHeight);
    
    ctx.strokeStyle = '#000';
    ctx.lineWidth = 2;

    // Draw vertical ladder lines
    console.log("Drawing vertical lines...");
    for (let i = 0; i <= numPlayers; i++) {
      ctx.beginPath();
      ctx.moveTo(i * stepWidth, 0);
      ctx.lineTo(i * stepWidth, ladderHeight);
      ctx.stroke();
    }

    // Draw horizontal ladder lines
    console.log("Drawing horizontal lines...");
    ladderLines.forEach(line => {
      ctx.beginPath();
      ctx.moveTo(line.start.x, line.start.y);
      ctx.lineTo(line.end.x, line.end.y);
      ctx.stroke();
    });

    // Draw players
    ctx.fillStyle = '#f00';
    console.log("Drawing players...");
    players.forEach((_, index) => {
      ctx.beginPath();
      ctx.arc(stepWidth / 2, (index + 1) * stepHeight, 10, 0, Math.PI * 2);
      ctx.fill();
    });

  }, [players]);

  return (
    <canvas
      ref={canvasRef}
      width={600}
      height={400}
      style={{ border: '1px solid #000' }}
    />
  );
};

export default CanvasLadder;
