import React, { useState,useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from "react-redux";
import { setMemo } from "../redux/actions/memoActions";
import * as S from './style';

function Memo() {
    const location = useLocation();
    const navigate = useNavigate();
    const [memos, setMemos] = useState([]);
    const [input, setInput] = useState('');
    const [text, setText] = useState('');

    const dispatch = useDispatch();
    const { memo_ } = useSelector(state => state.memos);

    const memo = (memos) => {
        dispatch(setMemo(memos));
    };

    const handleInputChange = (e) => {
        setInput(e.target.value);  
    };

    const handleTextChange = (e) => {
        setText(e.target.value);  
    };

    const handleCheckboxChange = (index) => {
        setMemos((prevMemos) =>
            prevMemos.map((memo, i) =>
                i === index ? { ...memo, checked: !memo.checked } : memo
            )
        );
    };

    const upload_memo = async (content) => {
        if (input && text) {
            const memoTime = new Date().toLocaleString();
            const newMemo = { title: input, content: text, name:text, created_at: memoTime };
            console.log('Sending data:', JSON.stringify(newMemo)); 
            try {
                const response = await fetch('http://localhost:5000/api/notes', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify(newMemo),
                });
    
                if (response.ok) {
                    const result = await response.json();
                    console.log(result)
                    setInput('');
                    setText('');
                    navigate('/memo');
                    dispatch(setMemo([...memos, result]));
                    console.log(memo)
                } else {
                    console.error('Failed to upload memo');
                }
            } catch (error) {
                console.error('Error:', error);
            }
        } else {
            console.log('err');
        }
    };
    
    const delete_button = async () => {
        const idsToDelete = memos.filter(memo => memo.checked).map(memo => memo.id);
    
        for (const id of idsToDelete) {
            try {
                const response = await fetch(`http://localhost:5000/api/notes/${id}`, {
                    method: 'DELETE',
                });
    
                if (response.ok) {
                    console.log(`Deleted memo with id ${id}`);
                } else {
                    console.error(`Failed to delete memo with id ${id}`);
                }
            } catch (error) {
                console.error('Error:', error);
            }
        }
    
        // 삭제된 메모를 제외한 나머지를 상태에 유지
        setMemos(memos.filter(memo => !memo.checked));
    };
    

    console.log(memos);

    useEffect(() => {
        localStorage.setItem("name", JSON.stringify(memos));
        
        const saved = localStorage.getItem("name");
        if (saved !== null) {
          setMemos(saved);
        }
      }, [memos]);

    return (
        <S.Components>
            <div className='background'>
                {location.pathname === '/memo' ? (
                    <>
                        <div className='bar'>
                            <div className='left'>
                                <Link to={'/memo/input'}>
                                    <button className="w-btn w-btn-indigo" type="button">
                                        memo
                                    </button>
                                </Link>
                            </div>
                            <div className='right'>
                                <div className='s'>
                                    <button onClick={delete_button} className="w-btn w-btn-pink" type="button">
                                        Delete
                                    </button>
                                </div>
                            </div>
                        </div>
                        <div className='memo'>
                            {memos.length > 0 ? (
                                memos.map((memo, index) => (
                                    <div key={index} className='memo-item'>
                                        <ul>
                                            <li>
                                                <div className='front'>
                                                    <input 
                                                        type='checkbox' 
                                                        checked={memo.checked} 
                                                        onChange={() => handleCheckboxChange(index)} 
                                                    />
                                                    <div className='m_title'>{memo.title}</div>
                                                </div>
                                                <div className='m_time'>{memo.time}</div>
                                            </li>
                                        </ul> 
                                    </div>
                                ))
                            ) : (
                                "No memos available"
                            )}
                        </div>
                    </>
                ) : (
                    <>
                        <div className='bar'>
                            <div className='left'>
                                <Link to={'/memo'}>
                                    <button className="w-btn w-btn-indigo" type="button">
                                        back
                                    </button>
                                </Link>
                            </div>
                            <div className='right'>
                                <div className='a'>
                                    <button className="w-btn w-btn-pink" type="button" onClick={upload_memo}>
                                        upload
                                    </button>
                                </div>
                            </div>
                        </div>
                        <div className='title'>
                            <input
                                className='text_title'
                                type='text'
                                placeholder="Title"
                                alt='Title'
                                value={input}
                                onChange={handleInputChange}
                            />
                            <textarea
                                className='content'
                                placeholder='Content'
                                value={text}
                                onChange={handleTextChange}
                            />
                            <div className='upload' type='button' onClick={upload_memo}>
                                Upload
                            </div>
                        </div>
                    </>
                )}
            </div>
        </S.Components>
    );
}

export default Memo;
