import './App.css';
import React ,{useEffect}from 'react';
import Naver from './Navbar/index';
import Home from './Home/index';
import Memo from './Memo';
import Loading from './Loading';
import { Routes, Route } from 'react-router-dom';
import {  setUserDetails,setLoading,setError } from './redux/actions/UserAction';
import { useDispatch, useSelector } from 'react-redux';
import Login from './Login';
import Members from './members';


function App() {
  const dispatch = useDispatch();
  const { userDetails =[], isLoading } = useSelector(state => state.user) ;

  useEffect(() => {

  if(userDetails){
  const mainApi = async (token) => {
    dispatch(setLoading(true));
    dispatch(setError(null));

    try {
        const response = await fetch(`http://localhost:5000/api/login`, {
            method: 'GET',
            headers: {
              'Authorization': `Bearer ${token}`,
              'Content-Type': 'application/json',
            },
        });

        const data = await response.json();

        dispatch(setUserDetails(data.user));
        console.log('User details:', data.user);
        if (response.ok) {
          console.log('Protected data:', data);
        } else {
          console.log('Failed to fetch protected data:', data.message);
        }
      }  catch (err) {
      console.error('Error fetching protected data:', err);
    }
    mainApi();
};
  }
  }, [userDetails]);

  return (
    <div className='header'>
      <Naver />

      <div className='body'>

        {isLoading  ?<Loading/>: null} 
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/memo/*" element={<Memo />} />
          <Route path="/login" element={<Login/>}/>
          <Route path='/login/members' element={<Members/>}/>
        </Routes>
      </div>
    </div>
  );
}

export default App;
