import './App.css';
import React ,{useEffect}from 'react';
import Naver from './Navbar/index';
import Home from './Home/index';
import Memo from './Memo';
import Loading from './Loading';
import { Routes, Route, Link } from 'react-router-dom';
import { setUserDetails, setLoading,setError } from './redux/actions/UserAction';
import { useDispatch, useSelector } from 'react-redux';
import Login from './Login';
import Members from './members';


function App() {
  const dispatch = useDispatch();
  const { userDetails =[], isLoading } = useSelector(state => state.user) ;

  useEffect(() => {

  if(userDetails){
  const mainApi = async () => {
    dispatch(setLoading(true));
    dispatch(setError(null));

    try {
        const response = await fetch(`http://localhost:5000/api/member`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(userDetails),
        });

        const result = await response.json();
        console.log('mainData', result);
        dispatch(setLoading(false));
    } catch (error) {
        dispatch(setError(error.message));
        window.alert(error.message);
        dispatch(setLoading(false));
    }
    mainApi();
};
  }
  }, [userDetails]);

  return (
    <div className='header'>
      <Naver />

      <div className='body'>

        {isLoading  ?<Loading/>: null} 
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/memo/*" element={<Memo />} />
          <Route path="/login" element={<Login/>}/>
          <Route path='/login/members' element={<Members/>}/>
        </Routes>
      </div>
    </div>
  );
}

export default App;
