import React, { useState } from 'react';
import { useSelector } from "react-redux";
import Canvas from './Canvas'; // 캔버스 컴포넌트

function Game() {
  const { data } = useSelector((state) => state.imgdata); // 'imgdata'를 사용하여 정확한 state 접근
  const [text, setText] = useState('');

  return (
    <div className='parent_body'>
      {/* Canvas는 반복문 외부에 위치시켜 한 번만 렌더링 */}
      <div className='canvas_wrapper'>
        <Canvas />
      </div>

      <div className='data_body'>
        {data.map((imgs, index) => (
          <div className='parent' key={index}>
            <img src={imgs.src} alt={imgs.alt} />
            <input 
              type='text'
              value={text}
              onChange={(e) => setText(e.target.value)}
            />
          </div>
        ))}
      </div>
    </div>
  );
}

export default Game;
