import React from 'react';
import { Link, useParams } from 'react-router-dom';

function DataInput({ memos }) {
    const { title } = useParams();
    const memo = memos.find(memo => memo.title === title); 

    if (!memo) {
        return <div>Memo not found</div>; 
    }

    return (
        <>
            <div className='bar'>
                <Link to='/memo'>
                    <button className="w-btn w-btn-indigo" type="button">Back</button>
                </Link>
                <button className="w-btn w-btn-pink" type="button">Correct</button>
            </div>
            <div className='title'>
                <div className='title_name'>{memo.title}</div>
                <div className='content'>{memo.content}</div>
            </div>
        </>
    );
}

export default DataInput;
