import React, { useState, useRef, useEffect } from 'react';

const Canvas = ({ data, imgRefs }) => {
  const canvasRef = useRef(null);
  const [width, setWidth] = useState(window.innerWidth - 100);
  const [animatedPaths, setAnimatedPaths] = useState([]);
  const animationFrameId = useRef(null);  // Animation ID 저장
  const [isPaused, setIsPaused] = useState(false); // 애니메이션 상태 관리
  const [tolerance] = useState(1);

  
  useEffect(() => {
    const updateCanvasSize = () => {
      setWidth(window.innerWidth - 100);
    };

    window.addEventListener('resize', updateCanvasSize);
    updateCanvasSize();
    return () => window.removeEventListener('resize', updateCanvasSize);
  }, []);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const ladderHeight = 600;
    const ladderY = 50;

    ctx.clearRect(0, 0, canvas.width, canvas.height);
    const imgPositions = [];

    data.forEach((playerData, index) => {
      const img = imgRefs.current[index];
      if (img) {
        const rect = img.getBoundingClientRect();
        const canvasRect = canvas.getBoundingClientRect();
        const imgX = rect.left - canvasRect.left + img.width / 2;
        imgPositions.push(imgX);

        // Draw vertical line
        ctx.beginPath();
        ctx.moveTo(imgX, 0);
        ctx.lineTo(imgX, ladderHeight);
        ctx.stroke();
      }
    });

    const minHorizontalLines = data.length - 1;
    const horizontalLines = minHorizontalLines + Math.ceil(Math.random() * 5);

    const newAnimatedPaths = [];
    
    const startYValues = [];
    for (let i = 0; i < horizontalLines; i++) {
      const x1 = imgPositions[i % imgPositions.length];
      const x2 = imgPositions[(i + 1) % imgPositions.length];
      let startY = ladderY + Math.random() * ladderHeight;
      // Draw horizontal line
      ctx.beginPath();
      ctx.moveTo(x1, startY);
      ctx.lineTo(x2, startY);
      ctx.stroke();
      startYValues.push(startY);
      
      if (newAnimatedPaths.length < data.length) {
        newAnimatedPaths.push({
          startYValues: [...startYValues],
          startX: Math.round(x1),
          currentX: Math.round(x1),
          targetX: Math.round(x2),
          y: Math.round(startY),
          currentY: 0,
          direction: 'down',
          horizontalStepSize: 5,
          stepSize: 5,
          horizontalDirection: x2 > x1 ? 'right' : 'left',
          endY: ladderHeight  // 수직선 끝 위치 저장
        });
      }
    }

    setAnimatedPaths(newAnimatedPaths);

  }, [width, data, imgRefs.current]);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const colors = ['red', 'blue', 'green', 'orange', 'pink'];

    const animate = () => {
      let allPathsCompleted = true;

      if (isPaused) {
        animationFrameId.current = requestAnimationFrame(animate);
        return; // 애니메이션이 일시 중지되면 계속 대기
      }
      animatedPaths.forEach((path, index) => {
        const { currentX, currentY, direction, stepSize, horizontalStepSize, horizontalDirection, targetX, endY, startYValues } = path;
    
        ctx.strokeStyle = colors[index % colors.length];
        ctx.lineWidth = 3;
    
        // Use a tolerance for comparison
        const tolerance = 0.5;
        const nextYIndex = startYValues.findIndex(yVal => currentY + tolerance < yVal);
        const nextY = nextYIndex !== -1 ? startYValues[nextYIndex] : endY;
    
        // Log nextYIndex and nextY to verify the behavior
        console.log(`currentY: ${Math.round(path.currentY)}, nextYIndex: ${nextYIndex}, nextY: ${Math.round(nextY)}`);
    
        if (direction === 'down') {
          ctx.beginPath();
          ctx.moveTo(currentX, currentY);
          path.currentY = Math.min(currentY + stepSize, nextY);  // 다음 수평선까지 이동
          ctx.lineTo(currentX, path.currentY);
          ctx.stroke();
    
         if (Math.abs(currentY - nextY) < tolerance) {
          path.currentY = nextY;
          path.direction = 'horizontal'; // 수평 이동으로 전환
          path.currentX = currentX;
          setIsPaused(true); // 애니메이션 일시 정지
          setTimeout(() => setIsPaused(false), 500); // 500ms 후 애니메이션 재개
        }
        } else if (direction === 'horizontal') {
          ctx.lineWidth = 4;
          ctx.beginPath();
          ctx.moveTo(currentX, nextY);
          const nextX = currentX + (horizontalDirection === 'right' ? horizontalStepSize : -horizontalStepSize);
          ctx.lineTo(nextX, nextY);
          ctx.stroke();
    
          if ((horizontalDirection === 'right' && currentX >= targetX) || (horizontalDirection === 'left' && currentX <= targetX)) {
            path.direction = 'down';  // 다시 수직으로 전환
          }
        }
    
        if (path.currentY < endY) {
          allPathsCompleted = false;  // 아직 완료되지 않은 path가 있음
        }
      });
    
      if (!allPathsCompleted) {
        animationFrameId.current = requestAnimationFrame(animate);
      } else {
        cancelAnimationFrame(animationFrameId.current);
      }
    };
    
    animationFrameId.current = requestAnimationFrame(animate);

    return () => {
      cancelAnimationFrame(animationFrameId.current);
    };
  }, [animatedPaths]);

  return (
    <canvas
      ref={canvasRef}
      width={width}
      height={600}
    />
  );
};

export default Canvas;
