import { configureStore } from '@reduxjs/toolkit';
import memoReducer from './reducers/MemoReducer'; // rootReducer를 가져옵니다.

const store = configureStore({
  memo: memoReducer, // 루트 리듀서를 설정합니다.
});

export default store;
