// Index.js
import React from 'react';
import Start from './Start'; // Start 컴포넌트 불러오기
import * as S from './style'; // 스타일 파일 불러오기
import { useSelector, useDispatch } from "react-redux";
import { setNumber, resetNumber, decrementNumber } from '../redux/actions/NumberAction';

function Index() {
  const { number, maxNumber, minNumber } = useSelector((state) => state.number); // 올바르게 상태를 선택합니다.
  const dispatch = useDispatch();
console.log(maxNumber)
  const plus = () => {
    if (number < 10) {
      dispatch(setNumber(number + 1));
    } else {
      alert('숫자가 최대값에 도달했습니다.');
    }
  };

  const min = () => {
    if (number > minNumber) {
      dispatch(decrementNumber(number - 1));
    } else {
      alert('숫자가 최소값에 도달했습니다.');
    }
  };

  const resetCounter = () => {
    dispatch(resetNumber());
  };

  return (
    <S.components>
      <div className='body'>
        <Start 
          number={number} 
          plus={plus} 
          miners={min} 
          minNumber={minNumber}
          maxNumber={maxNumber}
          resetNumber={resetCounter} 
        />
      </div>
    </S.components>
  );
}

export default Index;
