import styled from "styled-components";

export const components = styled.div`
    display: flex;
    justify-content: center;
.body {
  background-color: #fff;
  width: 500px;
  height: 300px;
  padding: 17px 178px 241px;
  display: flex;
  flex-direction: column;
}
.Start {
  font-size: 69px;
  display: flex;
  justify-content: center;
  gap: 21px;
  margin-bottom: 34px;
}

.plus {
  font-size: 40px;
  margin-left: 40px;
}
.miners {
  font-size: 40px;
  margin-right: 40px;
}
.game {
  width: 100%;
}
.parent_body {
  display: flex;
  gap: 30px;
  flex-direction: column;
  position: relative; 
}
.data_body {
  display: flex;
  gap: 70px;
  position: relative;
  z-index: 1; 
}
.parent {
  width: 100px;
  position: relative; 
}
.parent img {
  width: 100%;
  margin-bottom: 20px; 
}

input {
  margin-top: 10px;
}
.canvas_wrapper {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
  z-index: 0;
}
  .canvas_wrapper canvas {
  width: 100%;
  height: 100%;
}
`
