// redux/reducers/numberReducer.js
import { SET_NUMBER, RESET_NUMBER, DECREMENT_NUMBER } from '../actions/types';

const initialState = {
  number: 0,
  maxNumber: 10,
  minNumber: 0,
};

const numberReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_NUMBER:
      return { ...state, number: Math.min(action.payload, state.maxNumber) };
    case DECREMENT_NUMBER:
      return { ...state, number: Math.max(action.payload, state.minNumber) };
    case RESET_NUMBER:
      return { ...state, number: initialState.number };
    default:
      return state;
  }
};

export default numberReducer;
