// reducers/numberReducer.js
import { SET_NUMBER, RESET_NUMBER } from '../actions/types';

const initialState = {
  number: 0, // 초기 숫자 상태
  maxNumber: 10, // 최대값 설정
};

const numberReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_NUMBER:
      if (action.payload > state.maxNumber) {
        return { ...state, number: state.maxNumber }; // 최대값을 초과하지 않도록 설정
      }
      return { ...state, number: action.payload };
    case RESET_NUMBER:
      return { ...state, number: initialState.number }; // 숫자를 초기 상태로 리셋
    default:
      return state;
  }
};

export default numberReducer;
